INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65533, 'Iron protein succinylate', 'Cimetidine', 'Minor', 'Limited data have suggested that concurrent administration of cimetidine may reduce the bioavailability of orally administered iron. Some investigators have suggested that cimetidine-induced increases in gastric pH may reduce the solubility of iron salts, which leads to decreased absorption. Based on existing evidence, no intervention (e.g., separating times of administration) appears to be necessary during concomitant use of cimetidine and iron products unless an interaction is suspected.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65620/', '[1] "Product Information. Tagamet (cimetidine)." SmithKline Beecham, Philadelphia, PA.[2] Partlow ES, Campbell NRC, Chan SC, Pap KM, Granberg K, Hasinoff BB "Ferrous sulfate does not reduce serum levels of famotidine or cimetidine after concurrent ingestion." Clin Pharmacol Ther 59 (1996): 389-93[3] Bianchi FM, Cavassini GB, Leo P "Iron protein succynilate in the treatment of iron deficiency: potential interaction with H2-receptor antagonists." Int J Clin Pharmacol Ther Toxicol 31 (1993): 209-17[4] Rosner F "Cimetidine and iron absorption." Lancet 1 (1978): 95', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65534, 'Isavuconazonium', 'Cimetidine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of isavuconazole, which is primarily metabolized by CYP450 3A4 and 3A5 and subsequently by uridine diphosphate glucuronosyltransferases (UGT).', NULL, 'Caution is advised when isavuconazonium sulfate is prescribed with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when isavuconazonium sulfate is prescribed with CYP450 3A4 inhibitors. Patients should be monitored for adverse effects such as nausea, vomiting, diarrhea, peripheral edema, hypokalemia, hypomagnesemia, and hepatotoxicity. In addition, many CYP450 3A4 inhibitors are also substrates of the isoenzyme, thus pharmacologic response to these agents should also be monitored, as isavuconazole itself is reportedly a moderate CYP450 3A4 inhibitor.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65621/', '[1] "Product Information. Cresemba (isavuconazonium)." Astellas Pharma US, Inc, Deerfield, IL.', 1767363022);
