INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65554, 'Cimetidine', 'Lomustine', 'Major', 'Coadministration with cimetidine may potentiate the myelosuppressive effects of carmustine and lomustine. The exact mechanism of interaction is unknown, but may involve additive myelosuppression or inhibition by cimetidine of the hepatic clearance of the nitrosoureas.', NULL, 'Caution is advised if carmustine or lomustine is used in combination with cimetidine.', NULL, 'Caution is advised if carmustine or lomustine is used in combination with cimetidine. Patients should be closely monitored for the development of delayed myelosuppression that may occur 4 to 6 weeks after administration of the nitrosourea. Blood counts are recommended weekly for at least 6 weeks after a dose. Alternatively, other H2-receptor antagonists such as ranitidine and famotidine may be considered in patients who require treatment with carmustine or lomustine, since they have not been reported to cause the interaction and generally have minimal effects on hepatic metabolism.', 'Metabolism, Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65641/', '[1] "Product Information. Gliadel Wafer (carmustine)." Rhone-Poulenc Rorer, Collegeville, PA.[2] Hess WA, Kornblith PL "Combination of lomustine and cimetidine in the treatment of a patient with malignant glioblastoma: a case report." Cancer Treat Rep 69 (1985): 733[3] Feagin OT "Alternative mechanisms for severe neutropenia." Arch Intern Med 142 (1982): 1971[4] "Product Information. CeeNU (lomustine)." Bristol-Myers Squibb, Princeton, NJ.[5] "Product Information. BiCNU (carmustine)." Bristol-Myers Squibb, Princeton, NJ.[6] Volkin RL, Shadduck RK, Winkelstein A, Zeigler ZR, Selker RG "Potentiation of carmustine-cranial irradiation-induced myelosuppression by cimetidine." Arch Intern Med 142 (1982): 243-5[7] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[8] Selker RG, Moore P, LoDolce D "Bone-marrow depression with cimetidine plus carmustine." N Engl J Med 299 (1978): 834', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65555, 'Cimetidine', 'Lonafarnib', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of lonafarnib. In vitro, lonafarnib has been shown to be primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 1A2, 2A6, 2C8, 2C9, 2C19, and 2E1.', NULL, 'Concomitant use of lonafarnib with weak CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of lonafarnib with weak CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, the manufacturer recommends reducing to or continuing lonafarnib at the starting dosage of 115 mg/m2 twice daily (for patients 12 months of age or older with a body surface area of at least 0.39 m2). Patients should be closely monitored for common adverse effects as well as arrhythmias and events such as syncope and heart palpitations, since lonafarnib exposures may be increased despite the dosage reduction and the effect on the QT interval is unknown. The previous dosage of lonafarnib may be resumed 14 days after discontinuing the weak CYP450 3A inhibitor.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65642/', '[1] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.[2] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65556, 'Cimetidine', 'Lorlatinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of lorlatinib, which has been found to be metabolized primarily by CYP450 3A4 and UGT1A4 in vitro. Increased exposure to lorlatinib may increase the risk of central nervous system adverse effects (e.g., seizures, hallucinations, changes in cognitive function, mood disorders, mental status changes, sleep impairment), hyperlipidemia (hypercholesterolemia and hypertriglyceridemia), PR interval prolongation and atrioventricular (AV) block, and interstitial lung disease/pneumonitis. The extent to which other, less potent CYP450 3A4 inhibitors may affect lorlatinib is unknown.', NULL, 'Caution is advised when lorlatinib is used with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when lorlatinib is used with CYP450 3A4 inhibitors. Patients should be monitored for the development of central nervous system and other adverse effects, and the dosing adjusted or discontinued in accordance with the lorlatinib product labeling.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65643/', '[1] "Product Information. Lorbrena (lorlatinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Lorbrena (lorlatinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65557, 'Cimetidine', 'Lovastatin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of HMG-CoA reductase inhibitors (i.e., statins) that are metabolized by the isoenzyme. Lovastatin and simvastatin are particularly susceptible because of their low oral bioavailability, but others such as atorvastatin and cerivastatin may also be affected.', NULL, 'Caution is recommended if atorvastatin, cerivastatin, lovastatin, simvastatin, or red yeast rice (which contains lovastatin) is prescribed with a CYP450 3A4 inhibitor.', NULL, 'Caution is recommended if atorvastatin, cerivastatin, lovastatin, simvastatin, or red yeast rice (which contains lovastatin) is prescribed with a CYP450 3A4 inhibitor. It is advisable to monitor lipid levels and use the lowest effective statin dose. All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed. Fluvastatin, pravastatin, and rosuvastatin are not expected to interact with CYP450 3A4 inhibitors.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65644/', '[1] Yeo KR, Yeo WW, Wallis EJ, Ramsay LE "Enhanced cholesterol reduction by simvastatin in diltiazem-treated patients." Br J Clin Pharmacol 48 (1999): 610-5[2] Andreou ER, Ledger S "Potential drug interaction between simvastatin and danazol causing rhabdomyolysis."