INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65603, 'Cimetidine', 'Nortriptyline', 'Moderate', 'Cimetidine may increase the serum concentration of tricyclic antidepressants (TCAs) to toxic levels in some patients. The mechanism may be related to inhibition of CYP450 isoenzymes, including CYP450 2D6 and CYP450 3A4, that are responsible for the hepatic metabolism of TCAs.', NULL, 'Close monitoring for clinical response and tolerance is recommended whenever cimetidine is added to or discontinued from a TCA regimen.', NULL, 'Close monitoring for clinical response and tolerance is recommended whenever cimetidine is added to or discontinued from a TCA regimen. Patients should be advised to notify their physician if they experience excessive TCA effects such as dry mouth, blurry vision, irregular or fast heartbeat, constipation, urinary retention, dizziness, or orthostatic hypotension. Lower doses of tricyclic antidepressants may be necessary. Ranitidine, famotidine, and nizatidine are not expected to interact and may be an alternative to cimetidine in this circumstance.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65690/', '[1] Abernethy DR, Greenblatt DJ, Shader RI "Imipramine-cimetidine interaction: impairment of clearance and enhanced absolute bioavailability." J Pharmacol Exp Ther 229 (1984): 702-5[2] Sutherland DL, Remillard AJ, Haight KR, Brown MA, Old L "The influence of cimetidine versus ranitidine on doxepin pharmacokinetics." Eur J Clin Pharmacol 32 (1987): 159-64[3] Henauer SA, Hollister LE "Cimetidine interaction with imipramine and nortriptyline." Clin Pharmacol Ther 35 (1984): 183-7[4] Cerner Multum, Inc. "UK Summary 