INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65617, 'Cimetidine', 'Paroxetine', 'Moderate', 'The coadministration with cimetidine may increase the plasma concentrations of paroxetine. The mechanism is inhibition of hepatic first-pass metabolism of paroxetine by cimetidine.', NULL, 'During concomitant administration with cimetidine, the possibility of prolonged and/or increased pharmacologic effects of paroxetine should be considered, particularly in susceptible patients such as the elderly or debilitated.', NULL, 'During concomitant administration with cimetidine, the possibility of prolonged and/or increased pharmacologic effects of paroxetine should be considered, particularly in susceptible patients such as the elderly or debilitated. Pharmacologic response to paroxetine should be monitored more closely whenever cimetidine is added to or withdrawn from therapy in patients stabilized on their paroxetine regimen, and the dosage adjusted as necessary. Alternatively, other H2-receptor antagonists such as famotidine, nizatidine, and ranitidine may be considered, since they are unlikely to interact with paroxetine.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65704/', '[1] Bannister SJ, Houser VP, Hulse JD, Kisicki JC, Rasmussen JG "Evaluation of the potential for interactions of paroxetine with diazepam, cimetidine, warfarin, and digoxin." Acta Psychiatr Scand Suppl 350 (1989): 102-6[2] Boyer WF, Blumhardt CL "The safety profile of paroxetine." J Clin Psychiatry 53 Suppl (1992): 61-6[3] Greb WH, Buscher G, Dierdorf HD, Koster FE, Wolf D, Mellows G "The effect of liver enzyme inhibition by cimetidine and enzyme induction by phen