INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65674, 'Cimetidine', 'Selpercatinib', 'Major', 'Coadministration with drugs that increase gastric pH may significantly decrease the oral bioavailability of selpercatinib and reduce its concentrations in plasma.', NULL, 'Concomitant use of selpercatinib with H2-receptor antagonists should generally be avoided.', NULL, 'Concomitant use of selpercatinib with H2-receptor antagonists should generally be avoided. If coadministration is required, the manufacturer recommends taking selpercatinib 2 hours before or 10 hours after the H2 receptor antagonist to minimize the clinical impact of the interaction.', 'Absorption', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65761/', '[1] "Product Information. Retevmo (selpercatinib)." Lilly, Eli and Company, Indianapolis, IN.[2] "Product Information. Retevmo (selpercatinib)." Lilly, Eli and Company, Indianapolis, IN.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65675, 'Cimetidine', 'Selumetinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of selumetinib, which is primarily metabolized by CYP450 3A4 and to a lesser extent by CYP450 2C19, 1A2, 2C9, 2E1, and 3A5. Selumetinib also undergoes glucuronidation by UGT1A1 and UGT1A3. Increased exposures to selumetinib and N-desmethyl selumetinib may increase the risk and/or severity of serious adverse effects such as cardiomyopathy (decrease in left ventricular ejection fraction by 10% or more below baseline), ocular toxicity (blurred vision, photophobia, cataracts, ocular hypertension, retinal pigment epithelial detachment, retinal vein occlusion), gastrointestinal toxicity (diarrhea, colitis), skin toxicity (dermatitis acneiform, maculopapular rash, eczema), and musculoskeletal toxicity (creatine phosphokinase elevations, myalgia, rhabdomyolysis).', NULL, 'Caution is advised when selumetinib is coadministered with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when selumetinib is coadministered with CYP450 3A4 inhibitors. Dosage adjustments may be required based on clinical response and tolerance. Please refer to the product labeling for specific guidelines on dosing adjustments.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65762/', '[1] "Multum Information Services, Inc. Expert Review Panel"[2] "Multum Information Services, Inc. Expert Review Panel"[3] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65676, 'Cimetidine', 'Sibutramine', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of sibutramine and its active metabolites, which are metabolized by the isoenzyme. Inhibitors such as ketoconazole, cimetidine, and erythromycin have caused moderate increases in the peak plasma concentration (Cmax) and area under the concentration-time curve (AUC) of the active metabolites.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65763/', '[1] "Product Information. Meridia (sibutramine)." Knoll Pharmaceutical Company, Whippany, NJ.[2] Mendelson J, Jones RT, Upton R, Jacob P 3rd "Methamphetamine and ethanol interactions in humans." Clin Pharmacol Ther 57 (1995): 559-68[3] "Product Information. Didrex (benzphetamine)" Pharmacia and Upjohn, Kalamazoo, MI.[4] "Product Information. Suprenza (phentermine)." Akrimax Pharmaceuticals, Cranford, NJ.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65677, 'Cimetidine', 'Sildenafil', 'Moderate', 'Coadministration with cimetidine may increase the plasma concentrations of sildenafil. The mechanism is cimetidine inhibition of sildenafil metabolism via CYP450 isoenzymes in the intestinal wall and liver.', NULL, 'Based on the magnitude of the reported interaction, no dosage adjustment for sildenafil should be necessary during coadministration with cimetidine.', NULL, 'Based on the magnitude of the reported interaction, no dosage adjustment for sildenafil should be necessary during coadministration with cimetidine. However, patients should be advised to promptly notify their physician if they experience potential signs and symptoms of sildenafil toxicity such as pain or tightness in the chest or jaw, irregular heartbeat, nausea, shortness of breath, visual disturbances, syncope, or prolonged erection (greater than 4 hours).', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65764/', '[1] Wilner K, Laboy L, LeBel M "The effects of cimetidine and antacid on the pharmacokinetic profile of sildenafil citrate in healthy male volunteers." Br J Clin Pharmacol 53 Suppl 1 (2002): 31S-6S[2] "Product Information. Viagra (sildenafil)." Pfizer US Pharmaceuticals, New York, NY.[3] Hyland R, Roe GH, Jones BC, Smith DA "Identification of the cytochrome P450 enzymes involved in the N-demethylation of sildenafil." Br J Clin Pharmaacol 51 (2001): 239-48[4] Warrington JS, Shader RI, vonMoltke LL, Greenblatt DJ "In vitro biotransformation of sildenafil (Viagra): Identification of human cytochromes and potential drug interactions." Drug Metab Disposition 28 (2