INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65694, 'Cimetidine', 'Tamsulosin', 'Moderate', 'Coadministration with cimetidine may increase the plasma concentrations of tamsulosin. The proposed mechanism is cimetidine inhibition of tamsulosin metabolism via CYP450 3A4 and 2D6.', NULL, 'Caution is advised if tamsulosin is used concomitantly with cimetidine, particularly at a dosage higher than 0.', NULL, 'Caution is advised if tamsulosin is used concomitantly with cimetidine, particularly at a dosage higher than 0.4 mg/day. The potential for increased risk of adverse effects such as postural hypotension, syncope, and priapism should be considered. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their doctor if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery until they know how the medication affects them.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65781/', '[1] Kamimura H, Oishi S, Matsushima H, et al. "Identification of cytochrome P450 isozymes involved in metabolism of the alpha1-adrenoceptor blocker tamsulosin in human liver microsomes." Xenobiotica 28 (1998): 909-22[2] Franco-Salinas G, de la Rosette JJ, Michel MC "Pharmacokinetics and pharmacodynamics of tamsulosin in its modified-release and oral controlled absorption system formulations." Clin Pharmacokinet 49 (2010): 177-88[3] "Product Information. Flomax (tamsulosin)." Boehringer-Ingelheim, Ridgefield, CT.[4] "Product Information. Flomax (tamsulosin)." Boehringer-Ingelheim, Ridgefield, CT.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65695, 'Cimetidine', 'Tasimelteon', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 and/or 3A4 may increase the plasma concentrations of tasimelteon, which is primarily metabolized by these isoenzymes.', NULL, 'Caution is advised if tasimelteon is prescribed in combination with inhibitors of CYP450 1A2 and/or 3A4.', NULL, 'Caution is advised if tasimelteon is prescribed in combination with inhibitors of CYP450 1A2 and/or 3A4. Patients should be monitored for excessive sedation and other side effects.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65782/', '[1] "Product Information. Hetlioz (tasimelteon)." Vanda Pharmaceuticals Inc, Rockville, MD.[2] "Product Information. Hetlioz (tasimelteon)." Vanda Pharmaceuticals Inc, Rockville, MD.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65696, 'Cimetidine', 'Tazemetostat', 'Moderate', 'Coadministration with CYP450 3A4 inhibitors may increase the plasma concentrations of tazemetostat, which is primarily metabolized by the isoenzyme. Clinically, this interaction may result in an increased risk of the frequency or severity of adverse reactions due to tazemetostat such as hemorrhage, pleural effusion, skin infection, dyspnea, pain, and respiratory distress. Data are not available for other, less potent inhibitors.', NULL, 'Caution and clinical monitoring is recommended with the concomitant use of tazemetostat with CYP450 3A4 inhibitors.', NULL, 'Caution and clinical monitoring is recommended with the concomitant use of tazemetostat with CYP450 3A4 inhibitors. Patients should be advised to notify their health care professional if they experience signs or symptoms of possible tazemetostat-related adverse effects including secondary malignancies, hemorrhage, pleural effusion, skin infection, dyspnea, pain, and respiratory distress.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65783/', '[1] "Product Information. Tazverik (tazemetostat)." Epizyme, Inc, Cambridge, MA.[2] "Product Information. Tazverik (tazemetostat)." Epizyme, Inc, Cambridge, MA.[3] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65697, 'Cimetidine', 'Temsirolimus', 'Moderate', 'Coadministration of temsirolimus with inhibitors of CYP450 3A4 may increase the plasma concentrations of sirolimus, a major active metabolite of temsirolimus and known substrate of CYP450 3A4.', NULL, 'Caution is advised if temsirolimus is prescribed in combination with CYP450 3A4 inhibitors.', NULL, 'Caution is advised if temsirolimus is prescribed in combination with CYP450 3A4 inhibitors. Pharmacologic response to temsirolimus should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the temsirolimus dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65784/', '[1] "Product Information. Torisel (temsirolimus)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[2] "Product Information. Torisel (temsirolimus)." Wyeth-Ayerst Laboratories, Philadelphia, PA.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65698, 'Cimetidine', 'Terbinafine', 'Minor', 'Cimetidine reduces the clearance of terbinafine by 33%. The mechanism probably is inhibition of hepatic enzymes by cimetidine. This interaction is unlikely to be of clinical importance; however, the dosage of terbinafine should be adjusted as necessary.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65785/', '[1] "Product Information. Lamisil (terbinafine)." Sandoz Pharmaceuticals Corporation, East Hanover, NJ.[2] Long CC, Hill SA, Thomas RC, Johnston A, Smith SG, Kendall F, Finlay AY "Effect of terbinafine on the pharmacokinetics of cyclosporin in humans." J Invest Dermatol 102 (1994): 740-3', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65699, 'Cimetidine', 'Terfenadine', 'Major', 'Cimetidine inhibits the CYP450 isoenzymes and may interfere with metabolism of the antihistamines terfenadine and astemizole. The accumulation of either of these drugs may result in a prolongation of the QT interval and could lead to the development of ventricular arrhythmias such as torsades de pointes. One case of drug-induced torsades de pointes has been reported in a patient receiving cimetidine and terfenadine. The patient was also receiving other medications including chlorpheniramine, propoxyphene and acetaminophen.', NULL, 'Until more information is available, it may be best to avoid this combination.', NULL, 'Until more information is available, it may be best to avoid this combination. Cetirizine, fexofenadine, or loratadine may be safer antihistamine alternatives.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65786/', '[1] Honig PK, Wortham DC, Zamani K, Conner DP, Mullin JC, Cantilena LR "Effect of concomitant administration of cimetidine and ranitidine on the pharmacokinetics and electrocardiographic effects of terfenadine." Eur J Clin Pharmacol 45 (1993): 41-6[2] Woosley RL, Chen Y, Freiman JP, Gillis RA "Mechanism of the cardiotoxic actions of terfenadine." JAMA 269 (1993): 1532-6[3] Ng PW, Chan WK, Chan TYK "Torsade de pointes during the concomitant use of terfenadine and cimetidine." Aust N Z J Med 26 (1996): 120-1[4] Hey JA, Delprado M, Egan RW, Kreutner W "Terfenadine, astemizole, and ebastine produce QTc interval prolongation in an experimental model predictive of adverse clinical ECG effects." Ann Allergy Asthma Immunol