INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65707, 'Cimetidine', 'Tinidazole', 'Minor', 'Coadministration with cimetidine may increase the serum concentrations of nitroimidazoles. The interaction has been reported with metronidazole. The proposed mechanism is cimetidine inhibition of the hepatic metabolism of metronidazole. No data are available for tinidazole. This interaction is unlikely to be of clinical significance.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65794/', '[1] "Product Information. Tindamax (tinidazole)." Presutti Laboratories Inc, Arlington Heights, IL.[2] Gugler R, Jensen JC "Interaction between cimetidine and metronidazole." N Engl J Med 309 (1983): 1518-9[3] Loft S, Sonne J, Poulsen HE, Peterson KT, Jorgensen BG, Dossing M "Inhibition and induction of metronidazole and antipyrine metabolism." Eur J Clin Pharmacol 32 (1987): 35-41[4] Loft S, Dossing M, Sonne J, Dalhof K, Bjerrum K, Poulsen HE "Lack of effect of cimetidine on the pharmacokinetics and metabolism of a single oral dose of metronidazole." Eur J Clin Pharmacol 35 (1988): 65-8[5] Visapaa JP, Tillonen JS, Kaihovaara PS, Salaspuro MP "Lack of disulfiram-like reaction with metronidazole and ethanol." Ann Pharmacother 36 (2002): 971-4[6] Giannini AJ, DeFrance DT "Metronidazole and alcohol: potential for combinative abuse." J Toxicol Clin Toxicol 20 (1983): 509-15[7] Edwards DL, Fink PC, Van Dyke PO "Disulfiram-like reaction associated with intravenous trimethoprim-sulfamethoxazole and metronidazole." Clin Pharm 5 (1986): 999-1000[8] Williams CS, Woodcock KR "Do ethanol and metronidazole interact to produce a disulfiram-like reac