INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65708, 'Cimetidine', 'Tiotropium', 'Minor', 'Coadministration with cimetidine is not expected to significantly alter the pharmacokinetics of orally inhaled tiotropium. In one study, concomitant administration of cimetidine (400 mg three times daily) and tiotropium (14.4 mcg intravenously over 15 minutes) resulted in a 20% increase in the 4-hour area under the plasma concentration-time curve (AUC) and a 28% decrease in the renal clearance of tiotropium. Tiotropium peak plasma concentration (Cmax) and total amount excreted in urine over 96 hours were not affected. These changes are unlikely to be of clinical significance.', NULL, '-', NULL, '-', 'Excretion', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65795/', '[1] "Product Information. Spiriva (tiotropium)." Boehringer Ingelheim, Ridgefield, CT.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65709, 'Cimetidine', 'Tizanidine', 'Major', 'Coadministration with inhibitors of CYP450 1A2 may significantly increase the plasma concentrations and pharmacologic effects of tizanidine, which is a sensitive substrate of the isoenzyme.', NULL, 'Concomitant use of tizanidine with CYP450 1A2 inhibitors should generally be avoided.', NULL, 'Concomitant use of tizanidine with CYP450 1A2 inhibitors should generally be avoided. Otherwise, caution is advised if coadministration is required. Dosage adjustments may be necessary in patients who experience excessive adverse effects of tizanidine such as drowsiness, dizziness, lightheadedness, hypotension, and bradycardia.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65796/', '[1] Granfors MT, Backman JT, Neuvonen M, Ahonen J, Neuvonen PJ "Fluvoxamine drastically increases concentrations and effects of tizanidine: A potentially hazardous interaction." Clin Pharmacol Ther 75 (2004): 331-41[2] "Product Information. Vioxx (rofecoxib)." Merck & Co, Inc, West Point, PA.[3] "Product Information. Zelboraf (vemurafenib)." Genentech, South San Francisco, CA.[4] Granfors MT, Backman JT, Neuvonen M, Neuvonen PJ "Ciprofloxacin greatly increases concentrations and hypotensive effect of tizanidine by inhibiting its cytochrome P450 1A2-mediated presystemic metabolism." Clin Pharmacol Ther 76 (2004): 598-606[5] Momo K, Doki K, Hosono H, Homma M, Kohda Y "Drug interaction of tizanidine and fluvoxamine." Clin Pharmacol Ther 76 (2004): 509-10[6] Momo K, Homma M, Kohda Y, Ohkoshi N, Yoshizawa T, Tamaoka A "Drug interaction of tizanidine and ciprofloxacin: Case report