INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65720, 'Cimetidine', 'Trimipramine', 'Moderate', 'Cimetidine may increase the serum concentration of tricyclic antidepressants (TCAs) to toxic levels in some patients. The mechanism may be related to inhibition of CYP450 isoenzymes, including CYP450 2D6 and CYP450 3A4, that are responsible for the hepatic metabolism of TCAs.', NULL, 'Close monitoring for clinical response and tolerance is recommended whenever cimetidine is added to or discontinued from a TCA regimen.', NULL, 'Close monitoring for clinical response and tolerance is recommended whenever cimetidine is added to or discontinued from a TCA regimen. Patients should be advised to notify their physician if they experience excessive TCA effects such as dry mouth, blurry vision, irregular or fast heartbeat, constipation, urinary retention, dizziness, or orthostatic hypotension. Lower doses of tricyclic antidepressants may be necessary. Ranitidine, famotidine, and nizatidine are not expected to interact and may be an alternative to cimetidine in this circumstance.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65807/', '[1] Abernethy DR, Greenblatt DJ, Shader RI "Imipramine-cimetidine interaction: impairment of clearance and enhanced absolute bioavailability." J Pharmacol Exp Ther 229 (1984): 702-5[2] Sutherland DL, Remillard AJ, Haight KR, Brown MA, Old L "The influence of cimetidine versus ranitidine on doxepin pharmacokinetics." Eur J Clin Pharmacol 32 (1987): 159-64[3] Henauer SA, Hollister LE "Cimetidine interaction with imipramine and nortriptyline." Clin Pharmacol Ther 35 (1984): 183-7[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] Curry SH, DeVane CL, Wolfe MM "Lack of interaction of ranitidine with amitriptyline." Eur J Clin Pharmacol 32 (1987): 317-20[6] Cerner Multum, Inc. "Australian Product Information." O 0[7] Wells BG, Pieper JA, Self TH, et al "The effect of ranitidine and cimetidine on imipramine disposition." Eur J Clin Pharmacol 31 (1986): 285-90[8] Miller DD, Macklin M "Cimetidine-imipramine interaction: a case report." Am J Psychiatry 140 (1983): 351-2', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65721, 'Cimetidine', 'Troleandomycin', 'Moderate', 'Concomitant use of cimetidine and erythromycin may result in higher-than-normal plasma levels of erythromycin through inhibition of CYP450 metabolism by cimetidine.', NULL, 'The patient should be monitored for evidence of adverse effects such as nausea, vomiting, diarrhea, or abdominal pain.', NULL, 'The patient should be monitored for evidence of adverse effects such as nausea, vomiting, diarrhea, or abdominal pain. Ranitidine, nizatidine and famotidine do not inhibit CYP450 and would not be expected to affect erythromycin levels at recommended doses.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65808/', '[1] Mogford N, Pallett A, George C "Erythromycin deafness and cimetidine treatment." BMJ 309 (1994): 1620[2] "Product Information. Axid (nizatidine)." Lilly, Eli and Company, Indianapolis, IN.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] "Product Information. Zantac (ranitidine)." Glaxo Wellcome, Research Triangle Park, NC.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65722, 'Cimetidine', 'Trospium', 'Moderate', 'Theoretically, coadministration of trospium chloride with other drugs that are eliminated by active tubular secretion may result in increased plasma concentrations of trospium and/or the coadministered drug(s). The mechanism is competitive inhibition of renal excretion. Drugs that are thought to undergo active tubular secretion include acyclovir/valacyclovir, cidofovir, cimetidine, digoxin, flecainide, ganciclovir/valganciclovir, metformin, midodrine, morphine, pancuronium, procainamide, quinidine, ranitidine, tenofovir, triamterene, and vancomycin.', NULL, 'Patients receiving trospium chloride in combination with other drugs that undergo active tubular secretion should be monitored for excessive pharmacologic effects of one or both drugs, and the dosages of the drugs adjusted if necessary.', NULL, 'Patients receiving trospium chloride in combination with other drugs that undergo active tubular secretion should be monitored for excessive pharmacologic effects of one or both drugs, and the dosages of the drugs adjusted if necessary.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65809/', '[1] "Product Information. Sanctura (trospium chloride)." Odyssey Pharmaceutical, East Hanover, NJ.[2] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Sanctura (trospium chloride)." Odyssey Pharmaceutical, East Hanover, NJ.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65723, 'Cimetidine', 'Ubrogepant', 'Moderate', 'Coadministration with moderate or weak inhibitors of CYP450 3A4 may increase the plasma concentrations of ubrogepant, which is primarily metabolized by the isoenzyme.', NULL, 'The manufacturer recommends an initial ubrogepant dose of 50 mg when coadministered with moderate or weak CYP450 3A4 inhibitors.', NULL, 'The manufacturer recommends an initial ubrogepant dose of 50 mg when coadministered with moderate or weak CYP450 3A4 inhibitors. If needed, a second ubrogepant dose should be avoided within 24 hours of the initial dose when used with moderate CYP450 3A4 inhibitors.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65810/', '[1] "Product Information. Ubrelvy (ubrogepant)." Allergan Inc, Irvine, CA.[2] "Product Information. Ubrelvy (ubrogepant)." Allergan Inc, Irvine, CA.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65724, 'Cimetidine', 'Upadacitinib', 'Moderate', 'Coadministration with moderate inhibitors of CYP450 3A4 may increase the plasma concentrations of upadacitinib, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is recommended if upadacitinib is used with moderate CYP450 3A4 inhibitors.', NULL, 'Caution is recommended if upadacitinib is used with moderate CYP450 3A4 inhibitors. Pharmacologic effects of upadacitinib should be monitored more closely whenever a moderate CYP450 3A4 inhibitor is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65811/', '[1] "Product Information. Rinvoq (upadacitinib)." AbbVie US LLC, North Chicago, IL.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65725, 'Cimetidine', 'Valaciclovir', 'Minor', 'Cimetidine may increase the serum concentration of acyclovir. The mechanism is competitive inhibition of the renal tubular secretion of acyclovir. Initial dosage adjustments usually are not necessary. Patients should, however, be monitored for acyclovir-related toxicities. Also, cimetidine reduces the rate at which valacyclovir is converted to acyclovir. The extent of conversion is unaffected, and this interaction is not considered to be clinically significant.', NULL, '-', NULL, '-', 'Excretion', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65812/', '[1] Rolan PE, Maillot F, On NT, Posner J "The effects of cimetidine and probenecid on the conversion of a valine ester of acyclovir, 256U, to acyclovir and acyclovir renal clearance in healthy volunteers." Br J Clin Pharmacol 33 (1993): 533[2] "Product Information. Valtrex (valacyclovir)." Glaxo Wellcome, Research Triangle Park, NC.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65726, 'Cimetidine', 'Valdecoxib', 'Moderate', 'Coadministration with inhibitors of CYP450 2C9 and/or 3A4 may increase the plasma concentrations of valdecoxib, which is metabolized by these isoenzymes.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of valdecoxib or parecoxib, including serious adverse effects such as gastrointestinal ulceration and bleeding, should be considered during concomitant therapy with CYP450 2C9 or 3A4 inhibitors, particularly combination (2C9/3A4) inhibitors such as fluconazole, fluvoxamine, imatinib, and zafirlukast.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of valdecoxib or parecoxib, including serious adverse effects such as gastrointestinal ulceration and bleeding, should be considered during concomitant therapy with CYP450 2C9 or 3A4 inhibitors, particularly combination (2C9/3A4) inhibitors such as fluconazole, fluvoxamine, imatinib, and zafirlukast. Dose reductions of the COX-2 inhibitor may be required.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65813/', '[1] "Product Information. Bextra (valdecoxib)." Pharmacia Corporation, Peapack, NJ.', 1767363022);
