INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65736, 'Cimetidine', 'Zaleplon', 'Moderate', 'Concomitant administration of zaleplon and cimetidine resulted in an 85% increase in the area under the plasma concentration versus time curve for zaleplon. Cimetidine inhibits both aldehyde oxidase and CYP450 3A4, the primary enzymes responsible for metabolism of zaleplon.', NULL, 'If these drugs are used concurrently, the initial dose of zaleplon should be reduced to 5 mg.', NULL, 'If these drugs are used concurrently, the initial dose of zaleplon should be reduced to 5 mg. Patients should be monitored for excessive CNS depression, memory impairment, loss of coordination, and dizziness.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65823/', '[1] "Product Information. Sonata (zaleplon)" Wyeth-Ayerst Laboratories, Philadelphia, PA.[2] "Product Information. Sonata (zaleplon)" Wyeth-Ayerst Laboratories, Philadelphia, PA.', 1767363022);
