INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65808, 'Cinacalcet', 'Pirfenidone', 'Moderate', 'Coadministration with moderate or potent inhibitors of CYP450 2C9, 2C19, 2D6, and/or 2E1 may increase the plasma concentrations of pirfenidone, especially when used in addition to a moderate or potent CYP450 1A2 inhibitor. Pirfenidone is primarily (70% to 80%) metabolized by CYP450 1A2, with minor contribution from CYP450 2C9, 2C19, 2D6, and 2E1.', NULL, 'Concomitant use of pirfenidone with moderate or potent inhibitors of CYP450 2C9, 2C19, 2D6, and/or 2E1 in addition to a moderate or potent inhibitor of CYP450 1A2 (e.', NULL, 'Concomitant use of pirfenidone with moderate or potent inhibitors of CYP450 2C9, 2C19, 2D6, and/or 2E1 in addition to a moderate or potent inhibitor of CYP450 1A2 (e.g., certain fluoroquinolones, oral contraceptives, deferasirox, methoxsalen, mexiletine, thiabendazole, ticlopidine, zileuton) should be avoided. No particular precaution is necessary if pirfenidone and CYP450 2C9, 2C19, 2D6, and/or 2E1 inhibitors are coadministered without a moderate or potent CYP450 1A2 inhibitor.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65895/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Esbriet (pirfenidone)." Intermune Inc, Brisbane, CA.[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[4] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[5] "Product Information. Esbriet (pirfenidone)." Intermune Inc, Brisbane, CA.[6] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65809, 'Cinacalcet', 'Pitolisant', 'Major', 'Coadministration with strong inhibitors of CYP450 2D6 may increase plasma concentrations of pitolisant. The proposed mechanism is decreased clearance of pitolisant due to inhibition of its metabolism via this isoenzyme.', NULL, 'A dosage reduction is recommended for pitolisant during coadministration with strong inhibitors of CYP450 2D6.', NULL, 'A dosage reduction is recommended for pitolisant during coadministration with strong inhibitors of CYP450 2D6. For patients receiving a strong CYP450 2D6 inhibitor, the dose of pitolisant should be initiated at 8.9 mg once daily and increased after 7 days to a maximum dosage of 17.8 mg once daily. For patients already receiving a stable dose of pitolisant, the dose of pitolisant should be reduced by half when initiating treatment with a strong CYP450 2D6 inhibitor. Patients should be monitored for clinical response and increased adverse effects such as headache, insomnia, nausea, anxiety, increased heart rate, QT prolongation, hallucinations, abdominal pain, upper respiratory tract infections, and musculoskeletal pain.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65896/', '[1] "Product Information. Wakix (pitolisant)." Harmony Biosciences, LLC, Plymouth Meeting, PA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363022);
