INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65891, 'Darifenacin', 'Ciprofloxacin', 'Moderate', 'Coadministration with inhibitors of CYP450 2D6 and/or 3A4 may increase the plasma concentrations of darifenacin, which is a substrate of these isoenzymes.', NULL, 'Pharmacologic response to darifenacin should be monitored more closely whenever a CYP450 2D6 and/or 3A4 inhibitor is added to or withdrawn from therapy, and the darifenacin dosage adjusted if necessary.', NULL, 'Pharmacologic response to darifenacin should be monitored more closely whenever a CYP450 2D6 and/or 3A4 inhibitor is added to or withdrawn from therapy, and the darifenacin dosage adjusted if necessary. Patients should be advised to contact their physician if they experience undue adverse effects of darifenacin such as severe abdominal pain or constipation for 3 or more days.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65978/', '[1] "Product Information. Enablex (darifenacin)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65892, 'Darolutamide', 'Ciprofloxacin', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 and/or P-glycoprotein (P-gp) may increase the plasma concentrations of darolutamide, which is a substrate of both the isoenzyme and the efflux transporter. Caution is advised when darolutamide is used with CYP450 3A4 or P-gp inhibitors. Patients should be monitored more frequently for adverse effects such as fatigue, hypertension, neutropenia and abnormal liver function tests, and the darolutamide dosage adjusted as necessary in accordance with the product labeling.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65979/', '[1] "Product Information. Nubeqa (darolutamide)." Bayer HealthCare Pharmaceuticals Inc., Whippany, NJ.[2] "Product Information. Nubeqa (darolutamide)." Bayer HealthCare Pharmaceuticals Inc., Whippany, NJ.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65893, 'Dasatinib', 'Ciprofloxacin', 'Moderate', 'In vitro data suggest that dasatinib has the potential to prolong QT interval of the electrocardiogram. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution is recommended if dasatinib is used in combination with cumulative high-dose anthracycline therapy or other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if dasatinib is used in combination with cumulative high-dose anthracycline therapy or other drugs that can prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65980/', '[1] "Product Information. Sprycel (dasatinib)." Bristol-Myers Squibb, Princeton, NJ.[2] "Product Information. Sprycel (dasatinib)." Bristol-Myers Squibb, Princeton, NJ.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] Harper KM, Knapp DJ, Criswell HE, Breese GR "Vasopressin and alcohol: A multifaceted relationship." Psychopharmacology (Berl) 235 (2018): 3363-79[6] Cerner Multum, Inc. "Australian Product Information." O 0[7] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[8] Collins GB, Brosnihan K