INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65903, 'Didanosine', 'Ciprofloxacin', 'Moderate', 'INTERVAL: Coadministration with didanosine buffered tablets or pediatric oral solution may significantly reduce the oral bioavailability of ciprofloxacin and other quinolone antibiotics. The mechanism is reduced quinolone absorption due to chelation with metallic cations from buffering agents and antacids used in certain formulations of didanosine.', NULL, 'Quinolones such as ciprofloxacin should be administered at least 2 hours before or 6 hours after didanosine buffered tablets or pediatric oral solution, and patients should be monitored for potentially decreased antimicrobial efficacy during concomitant therapy.', NULL, 'Quinolones such as ciprofloxacin should be administered at least 2 hours before or 6 hours after didanosine buffered tablets or pediatric oral solution, and patients should be monitored for potentially decreased antimicrobial efficacy during concomitant therapy. Didanosine buffered powder for oral solution, which uses a citrate-phosphate buffer, and the delayed-release capsules, which are not buffered, are not expected to cause this interaction.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65990/', '[1] Hoffken G, Lode H, Wiley R, et al "Pharmacokinetics and bioavailability of ciprofloxacin and ofloxacin: effect of food and antacid intake." Rev Infect Dis 10(Suppl) (1988): S138-9[2] Polk RE "Drug-drug interactions with ciprofloxacin and other fluoroquinolones." Am J Med 87 (1989): s76-81[3] Deppermann KM, Lode H "Fluoroquinolones: interaction profile during enteral absorption." Drugs 45 Suppl 3 (1993): 65-72[4] 