INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65940, 'Eszopiclone', 'Ciprofloxacin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of both zopiclone and its pharmacologically active S(-) enantiomer, eszopiclone. Zopiclone has been shown in vitro to be metabolized by CYP450 3A4 and CYP450 2C8, while eszopiclone is primarily metabolized by CYP450 3A4 and 2E1 via demethylation and oxidation.', NULL, 'Caution is advised when zopiclone or eszopiclone is coadministered with moderate CYP450 3A4 inhibitors.', NULL, 'Caution is advised when zopiclone or eszopiclone is coadministered with moderate CYP450 3A4 inhibitors. A dosage reduction may be required if an interaction is suspected. Patients should be advised to avoid driving or operating hazardous machinery until they know how these medications affect them, preferably at least 12 hours after administration of the hypnotic.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/66027/', '[1] Becquemont L, Mouajjah S, Escaffre O, Beaune P, Funck-Bretano C, Jaillon P "Cytochrome P-450 3A4 and 2C8 are involved in zopiclone metabolism." Drug Metab Dispos 27 (1999): 1068-73[2] Dresser GK, Spence JD, Bailey DG "Pharmacokinetic-pharmacodynamic consequences and clinical relevance of cytochrome P450 3A4 inhibition." Clin Pharmacokinet 38 (2000): 41-57[3] "Product Information. Imovane (zopiclone)." Rhone-Poulenc Rorer Canada Inc, Laval, IN.[4] Aranko K, Luurila H, Backman JT, Neuvonen PJ, Olkkola KT "The effect of erythromycin on the pharmacokinetics and pharmacodymanics of zopiclone." Br J Clin Pharmacol 38 (1994): 363-7[5] "Product Information. Lunesta (eszopiclone)." Sepracor Inc, Marlborough, MA.[6] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[7] Alderman CP, Gebauer MG, Gilbert AL, Condon JT "Possible interaction of zopiclone and nefazodone." Ann Pharmacother 35 (2001): 1378-80[8] "Product Information. Lunesta (eszopiclone)." Sepracor Inc, Marlborough, MA.[9] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65941, 'Etacrynic acid', 'Ciprofloxacin', 'Minor', 'The plasma concentration of lomefloxacin following administration of furosemide has been reported to be higher than after lomefloxacin alone. The area under the curve of lomefloxacin was increased and the total and renal clearances of lomefloxacin were decreased. A similar interaction may occur if other quinolones are administered with other loop diuretics. The loop diuretic may decrease the renal clearance of the quinolone by competing for tubular secretion.', NULL, '-', NULL, '-', 'Excretion', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/66028/', '[1] Sudoh T, Fujimura A, Shiga T, Sasaki M, Harada K, Tateishi T, Ohashi K, Ebihara A "Renal clearance of lomefloxacin is decreased by furosemide." Eur J Clin Pharmacol 46 (1994): 267-9', 1767363022);
