INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65953, 'Fesoterodine', 'Ciprofloxacin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of fesoterodine''s active metabolite, 5-hydroxymethyl tolterodine, which is partially metabolized by the isoenzyme. The possibility of prolonged and/or increased pharmacologic effects of fesoterodine should be considered. Because 5-hydroxymethyl tolterodine is also metabolized by CYP450 2D6, the clinical significance of the interaction may be greater in patients who are CYP450 2D6-deficient, or so-called poor metabolizers of CYP450 2D6 (approximately 7% of Caucasians and less than 2% of Asians and individuals of African descent) who may rely more on the 3A4 metabolic pathway for clearance of the drug.', NULL, 'Close clinical and laboratory monitoring is advised whenever a CYP450 3A4 inhibitor is added to or withdrawn from fesoterodine therapy, and the dosage adjusted if necessary.', NULL, 'Close clinical and laboratory monitoring is advised whenever a CYP450 3A4 inhibitor is added to or withdrawn from fesoterodine therapy, and the dosage adjusted if necessary. Patients should be advised to notify their physician if they experience potential adverse effects of fesoterodine such as irregular heartbeat, blurry vision, difficulty urinating, dry mouth, headache, drowsiness, dizziness, gastrointestinal upset, or constipation.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/66040/', '[1] "Product Information. Toviaz (fesoterodine)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] "Product Information. Toviaz (fesoterodine)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65954, 'Fingolimod', 'Ciprofloxacin', 'Major', 'Due to its significant bradycardic effects, the risk of QT prolongation and torsade de pointes arrhythmia may be increased during initiation of fingolimod treatment in patients receiving drugs that prolong the QT interval. Fingolimod can cause a decrease in heart rate during initiation of therapy that is apparent within an hour of the first dose and maximal at approximately 6 hours postdose in most cases, but occasionally up to 20 hours after the first dose.', NULL, 'Fingolimod has not been studied in patients receiving drugs that can prolong the QT interval.', NULL, 'Fingolimod has not been studied in patients receiving drugs that can prolong the QT interval. Because bradycardia and AV block are recognized risk factors for QT prolongation and torsade de pointes arrhythmia, close monitoring is recommended during initiation of fingolimod treatment in patients receiving concomitant drugs that can prolong the QT interval, patients with significant QT prolongation (QTc >470 msec in females or >450 msec in males), or patients with relevant risk factors for QT prolongation (e.g., hypokalemia, hypomagnesemia, congenital QT prolongation).', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/66041/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[3] "Product Information. Gilenya (fingolimod)." Novartis Pharmaceuticals, East Hanover, NJ.[4] FDA. U.S. Food and Drug Administration "FDA Drug Safety Communication: Revised recommendations for cardiovascular monitoring and use of multiple sclerosis drug Gilenya (fingolimod). Available from: URL: http://www.fda.gov/Drugs/DrugSafety/ucm303192.htm#data." ([2012 May 14]):[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (65955, 'Flecainide', 'Ciprofloxacin', 'Moderate', 'Theoretically, concurrent use of two or more drugs that can cause QT interval prolongation may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. The risk of an individual agent or a combination of these agents causing ventricular arrhythmia in association with QT prolongation is largely unpredictable but may be increased by certain underlying risk factors such as congenital long QT syndrome, cardiac disease, and electrolyte disturbances.', NULL, 'Caution and clinical monitoring are recommended.', NULL, 'Caution and clinical monitoring are recommended. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/66042/', '[1] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing_000366.jsp&mid=WC0b01ac058067c852" ([2013 - ]):[2] Witchel HJ, Hancox JC, Nutt DJ "Psychotropic drugs, cardiac arrhythmia, and sudden death." J Clin Psychopharmacol 23 (2003): 58-77[3] Iannini PB "Cardiotoxicity of macrolides, ketolides and fluoroquinolones that prolong the QTc interval." Expert Opin Drug Saf 1 (2002): 121-8[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Ca