INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363139, 'Dexamethasone', 'Triazolam', 'Minor', 'Certain corticosteroids may decrease the plasma concentration of some benzodiazepines. The mechanism is related to induction of hepatic cytochrome P450 enzymes responsible for benzodiazepine metabolism. The clinical significance may depend on the dosage and duration of corticosteroid therapy and be of greater importance with oral administration of benzodiazepines.', 'DDInter', 1767317475, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
