INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363159, 'Dexamethasone', 'Vemurafenib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of vemurafenib, which has been shown in vitro to be a substrate of the isoenzyme.', 'DDInter', 1767317475, 'The potential for diminished pharmacologic effects of vemurafenib should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments or a dose adjustment of vemurafenib may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of vemurafenib should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363160, 'Dexamethasone', 'Venetoclax', 'Major', 'Coadministration with potent and moderate inducers of CYP450 3A4 may significantly decrease the plasma concentrations of venetoclax, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Concomitant use of venetoclax with potent and moderate CYP450 3A4 inducers should generally be avoided. Alternative agents with no or minimal CYP450 3A4 induction potential are recommended whenever possible.', 'Metabolism', 'Concomitant use of venetoclax with potent and moderate CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
