INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363162, 'Dexamethasone', 'Vigabatrin', 'Major', 'Theoretical concerns exist that oculotoxic effects of vigabatrin may be additive with those of other drugs that are associated with serious adverse ophthalmic effects. Vigabatrin causes permanent bilateral concentric visual field constriction in 30% or more of patients, with severity ranging from mild to severe. Vigabatrin can also damage the central retina and decrease visual acuity. The onset of vision loss is unpredictable, occurring within weeks of starting treatment to months or even years later. The risk of vision loss increases with increasing dose and cumulative exposure, although there is no dose or exposure known to be risk-free. It is possible that vision loss can worsen even after discontinuation of vigabatrin.', 'DDInter', 1767317475, 'Vigabatrin should generally not be used with other drugs associated with serious adverse ophthalmic effects such as retinopathy or glaucoma unless the benefits clearly outweigh the risks. These drugs may include amiodarone, deferoxamine, ethambutol, quinine derivatives, tamoxifen, and long-term corticosteroids. Vision testing at baseline (no later than 4 weeks after starting vigabatrin) and at least every 3 months during therapy is required for adults. Vision testing is also required about 3 to 6 months after the discontinuation of vigabatrin therapy. Due to the risk of irreversible vision loss, vigabatrin should be withdrawn from patients who fail to show substantial clinical benefit within 3 months of initiation, or sooner if treatment failure becomes obvious. The patient''s response and continued need for vigabatrin should be periodically reassessed. The lowest dosage and shortest duration of treatment consistent with clinical objectives should be employed.', 'Synergism', 'Vigabatrin should generally not be used with other drugs associated with serious adverse ophthalmic effects such as retinopathy or glaucoma unless the benefits clearly outweigh the risks.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363163, 'Dexamethasone', 'Vilazodone', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of vilazodone, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'The possibility of a diminished therapeutic response to vilazodone should be considered during coadministration with CYP450 3A4 inducers. Pharmacologic effects of vilazodone should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the dosage adjusted as necessary.', 'Metabolism', 'The possibility of a diminished therapeutic response to vilazodone should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363164, 'Dexamethasone', 'Vinblastine', 'Moderate', 'Coadministration with potent inducers of CYP450 3A4 and/or P-glycoprotein may significantly decrease the plasma concentrations of vinca alkaloids, which are substrates of both the hepatic microsomal isoenzyme and intracellular efflux transporter.', 'DDInter', 1767317475, 'The use of vinca alkaloids in combination with potent CYP450 3A4 and/or P-glycoprotein inducers such as carbamazepine, dexamethasone, phenobarbital, phenytoin, rifabutin, rifampin, rifapentine, and St. John''s wort should generally be avoided.', 'Metabolism', 'The use of vinca alkaloids in combination with potent CYP450 3A4 and/or P-glycoprotein inducers such as carbamazepine, dexamethasone, phenobarbital, phenytoin, rifabutin, rifampin, rifapentine, and St.', NULL, NULL, 'Moderate', 'pharmacodynamic');
