INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363196, 'Human C1-esterase inhibitor', 'Conjugated estrogens', 'Moderate', 'Thromboembolism may occur during treatment with C1 esterase inhibitors, and concurrent use of other agents that are also associated with this adverse effect can potentiate the risk.', 'DDInter', 1767317475, 'Caution is advised when C1 esterase inhibitors are used with other drugs that have thrombogenic effects such as androgens, estrogens, erythropoiesis-stimulating agents, or selective estrogen receptor modulators. Close monitoring for thromboembolic events is recommended during and after administration of C1 esterase inhibitors. Patients should be advised to seek medical attention if they develop potential signs and symptoms of thromboembolism such as chest pain; shortness of breath; rapid pulse; pain, swelling, and/or discoloration in an arm or leg; and numbness or weakness on one side of the body.', 'Synergism', 'Caution is advised when C1 esterase inhibitors are used with other drugs that have thrombogenic effects such as androgens, estrogens, erythropoiesis-stimulating agents, or selective estrogen receptor modulators.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363197, 'Conjugated estrogens', 'Calcipotriol (topical)', 'Moderate', 'Elevations in serum calcium levels have been observed in patients treated with topical vitamin D analogs. Concomitant use with systemic vitamin D products, calcium supplements, or other agents that can increase serum calcium concentrations such as thiazide diuretics, estrogens, anabolic steroids, and parathyroid hormone or parathyroid hormone analogs may increase the risk of clinically significant hypercalcemia.', 'DDInter', 1767317475, 'Calcium and vitamin D status should be monitored closely when topical vitamin D analogs are used in combination with systemic vitamin D products or other agents that can increase serum calcium levels.', 'Synergism', 'Calcium and vitamin D status should be monitored closely when topical vitamin D analogs are used in combination with systemic vitamin D products or other agents that can increase serum calcium levels.', NULL, NULL, 'Moderate', 'pharmacodynamic');
