INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363200, 'Conjugated estrogens', 'Carbamazepine', 'Moderate', 'RECOMMENDED: Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of estrogens and progestins. Estrogens have been shown in in vitro and in vivo studies to be partially metabolized by CYP450 3A4, and other steroids including progestins are also believed to undergo metabolism by this isoenzyme.', 'DDInter', 1767317475, 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary. For patients receiving hormonal contraceptives, additional or alternative non-hormonal birth control may be advisable during concomitant therapy with CYP450 3A4 inducers. Additional or alternative non-hormonal birth control may be recommended beyond discontinuation of the CYP450 3A4 inducer(s).', 'Metabolism', 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363201, 'Conjugated estrogens', 'Carfilzomib', 'Major', 'Venous thromboembolic events, including deep vein thrombosis (DVT) and pulmonary embolism (PE), have occurred during treatment with carfilzomib, and concurrent use of other agents also associated with this adverse effect can potentiate the risk.', 'DDInter', 1767317475, 'Caution is advised when carfilzomib is used with other drugs that can increase the risk of thrombosis. Thromboprophylaxis should be considered according to local treatment protocols. Close monitoring for thromboembolic events is recommended in patients with known risk factors (e.g., prior thrombosis, hyperlipidemia, hypertension, smoking) for thromboembolism. Patients should be advised to seek immediate medical attention if they develop symptoms of thromboembolism, such as shortness of breath, chest pain, hemoptysis, or arm or leg swelling or pain.', 'Synergism', 'Caution is advised when carfilzomib is used with other drugs that can increase the risk of thrombosis.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363202, 'Conjugated estrogens', 'Cenobamate', 'Moderate', 'RECOMMENDED: Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of estrogens and progestins. Estrogens have been shown in in vitro and in vivo studies to be partially metabolized by CYP450 3A4, and other steroids including progestins are also believed to undergo metabolism by this isoenzyme.', 'DDInter', 1767317475, 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary. For patients receiving hormonal contraceptives, additional or alternative non-hormonal birth control may be advisable during concomitant therapy with CYP450 3A4 inducers. Additional or alternative non-hormonal birth control may be recommended beyond discontinuation of the CYP450 3A4 inducer(s).', 'Metabolism', 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363203, 'Conjugated estrogens', 'Chenodeoxycholic acid', 'Moderate', 'Drugs like estrogens, oral contraceptives, fibrates, and possibly other lipid-lowering agents that increase biliary cholesterol secretion may potentiate the risk of cholesterol gallstones and counteract the effectiveness of chenodeoxycholic acid.', 'DDInter', 1767317475, 'Therapeutic effects of chenodeoxycholic acid should be closely monitored if concomitant use with estrogens, oral contraceptives, fibrates, and certain other lipid-lowering agents cannot be avoided.', 'Others', 'Therapeutic effects of chenodeoxycholic acid should be closely monitored if concomitant use with estrogens, oral contraceptives, fibrates, and certain other lipid-lowering agents cannot be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363204, 'Conjugated estrogens', 'Chlorpropamide', 'Moderate', 'The efficacy of insulin and other antidiabetic agents may be diminished by certain drugs, including atypical antipsychotics, corticosteroids, diuretics, estrogens, gonadotropin-releasing hormone agonists, human growth hormone, phenothiazines, progestins, protease inhibitors, sympathomimetic amines, thyroid hormones, L-asparaginase, alpelisib, copanlisib, danazol, diazoxide, isoniazid, megestrol, omacetaxine, phenytoin, tagraxofusp, temsirolimus, as well as pharmacologic dosages of nicotinic acid and adrenocorticotropic agents. These drugs may interfere with blood glucose control because they can cause hyperglycemia, glucose intolerance, new-onset diabetes mellitus, and/or exacerbation of preexisting diabetes.', 'DDInter', 1767317475, 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary. Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination.', 'Antagonism', 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363205, 'Conjugated estrogens', 'Cholestyramine', 'Moderate', 'Coadministration with bile-acid binding resins such as cholestyramine may decrease the plasma concentrations and therapeutic effects of estrogens and progestins. The proposed mechanism involves delaying or reducing the absorption of concomitant orally administered drugs by cholestyramine. In addition, it has also been suggested that bile-acid binding resins may interfere with the enterohepatic recycling of estrogens and therefore increase their elimination.', 'DDInter', 1767317475, 'Estrogens and/or progestin-containing oral medications should be administered at least one hour before or four to six hours after bile-acid binding resins such as cholestyramine, colestipol, and colesevelam. Patients receiving this combination should be advised to closely follow dosing schedules to prevent loss of therapeutic effects.', 'Absorption', 'Estrogens and/or progestin-containing oral medications should be administered at least one hour before or four to six hours after bile-acid binding resins such as cholestyramine, colestipol, and colesevelam.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363206, 'Conjugated estrogens', 'Cimetidine', 'Minor', 'Theoretically, pharmacologic effects of estrogens may be enhanced during treatment with cimetidine due to increased serum concentrations of endogenous estradiol. The mechanism is cimetidine inhibition of the 2-hydroxylation of estradiol.', 'DDInter', 1767317475, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363207, 'Conjugated estrogens', 'Clarithromycin', 'Moderate', 'RECOMMENDED: The effectiveness of estrogen-containing oral contraceptives may be impaired by concomitant treatment with antimicrobial agents. Most antimicrobials, with the exception of the rifamycins and possibly griseofulvin, do not induce hepatic enzymes and have not been shown to significantly increase the clearance of oral contraceptive estrogens. Certain macrolide antibiotics (e.g., erythromycin, clarithromycin) may increase the plasma concentrations and the risk of adverse effects of estrogens and progestogens. The mechanism is decreased clearance of the hormones due to inhibition of CYP450 3A4 activity by the macrolide antibiotic. Concomitant administration with erythromycin may increase the estradiol exposure by approximately 50%.', 'DDInter', 1767317475, 'Until further data are available, women using oral contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant antimicrobial therapy. During concomitant therapy with erythromycin or clarithromycin, patients should be observed for increased or altered pharmacologic response to estrogens and progestogens, and dosage(s) adjusted accordingly as necessary.', 'Metabolism', 'Until further data are available, women using oral contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant antimicrobial therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363208, 'Conjugated estrogens', 'Clomipramine', 'Minor', 'The effects of tricyclic antidepressants (TCAs) may be altered in women receiving estrogen-containing therapy. Simultaneous TCA toxicity and reduced effects have been reported. Akathisia has also been reported in some women taking this combination. The mechanism of interaction is unknown but may be related to increased TCA bioavailability or inhibition of hepatic TCA metabolism.', 'DDInter', 1767317475, 'The clinical significance of this interaction has not been established. Monitoring for altered effects may be advisable during concomitant therapy. Dose adjustments of the TCA may be required if an interaction is suspected.', 'Others', 'The clinical significance of this interaction has not been established.', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363209, 'Conjugated estrogens', 'Clotrimazole', 'Moderate', 'Azole antifungal agents may increase the plasma concentrations of estrogens and progestins. The mechanism is decreased clearance of the hormones due to inhibition of CYP450 3A4 activity by azole antifungals.', 'DDInter', 1767317475, 'During concomitant therapy with azole antifungal agents, patients should be observed for increased or altered pharmacologic response to estrogens and progestins, and dosage(s) adjusted accordingly as necessary.', 'Metabolism', 'During concomitant therapy with azole antifungal agents, patients should be observed for increased or altered pharmacologic response to estrogens and progestins, and dosage(s) adjusted accordingly as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
