INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363211, 'Conjugated estrogens', 'Conestat alfa', 'Moderate', 'Thromboembolism may occur during treatment with C1 esterase inhibitors, and concurrent use of other agents that are also associated with this adverse effect can potentiate the risk.', 'DDInter', 1767317475, 'Caution is advised when C1 esterase inhibitors are used with other drugs that have thrombogenic effects such as androgens, estrogens, erythropoiesis-stimulating agents, or selective estrogen receptor modulators. Close monitoring for thromboembolic events is recommended during and after administration of C1 esterase inhibitors. Patients should be advised to seek medical attention if they develop potential signs and symptoms of thromboembolism such as chest pain; shortness of breath; rapid pulse; pain, swelling, and/or discoloration in an arm or leg; and numbness or weakness on one side of the body.', 'Synergism', 'Caution is advised when C1 esterase inhibitors are used with other drugs that have thrombogenic effects such as androgens, estrogens, erythropoiesis-stimulating agents, or selective estrogen receptor modulators.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363212, 'Conivaptan', 'Conjugated estrogens', 'Moderate', 'Coadministration with conivaptan may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by conivaptan.', 'DDInter', 1767317475, 'Caution is advised if conivaptan must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever conivaptan is added to or withdrawn from therapy, or the combination avoided altogether.', 'Metabolism', 'Caution is advised if conivaptan must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363213, 'Conjugated estrogens', 'Corticotropin', 'Moderate', 'Estrogens may enhance the systemic effects of both endogenous and exogenous corticosteroids. The proposed mechanism is an increase in serum cortisol-binding globulin (transcortin) induced by estrogens, resulting in a decreased rate of corticosteroid metabolic clearance. The interaction has been reported with estrogens or estrogen-containing oral contraceptives (OCs) and hydrocortisone, prednisone, and prednisolone.', 'DDInter', 1767317475, 'Patients treated concomitantly with an estrogen-containing drug may require lower dosages of corticosteroids or adrenocorticotropic agents. Pharmacologic response to these agents should be monitored more closely whenever an estrogen is added to or withdrawn from therapy in patients stabilized on their existing corticosteroid or adrenocorticotropic regimen, and the dosage(s) adjusted as necessary.', 'Synergism', 'Patients treated concomitantly with an estrogen-containing drug may require lower dosages of corticosteroids or adrenocorticotropic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
