INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363238, 'Erythromycin', 'Conjugated estrogens', 'Moderate', 'RECOMMENDED: The effectiveness of estrogen-containing oral contraceptives may be impaired by concomitant treatment with antimicrobial agents. Most antimicrobials, with the exception of the rifamycins and possibly griseofulvin, do not induce hepatic enzymes and have not been shown to significantly increase the clearance of oral contraceptive estrogens. Certain macrolide antibiotics (e.g., erythromycin, clarithromycin) may increase the plasma concentrations and the risk of adverse effects of estrogens and progestogens. The mechanism is decreased clearance of the hormones due to inhibition of CYP450 3A4 activity by the macrolide antibiotic. Concomitant administration with erythromycin may increase the estradiol exposure by approximately 50%.', 'DDInter', 1767317475, 'Until further data are available, women using oral contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant antimicrobial therapy. During concomitant therapy with erythromycin or clarithromycin, patients should be observed for increased or altered pharmacologic response to estrogens and progestogens, and dosage(s) adjusted accordingly as necessary.', 'Metabolism', 'Until further data are available, women using oral contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant antimicrobial therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
