INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363256, 'Hemin', 'Conjugated estrogens', 'Major', 'Coadministration with drugs that are capable of increasing the activity of delta-aminolevulinic acid synthetase (e.g., estrogens, barbiturates, steroid metabolites, rifampin) may negate the pharmacologic effect of hemin. In the treatment of porphyria, hemin is thought to limit the rate of porphyrin/heme biosynthesis by inhibiting delta-aminolevulinic acid synthetase, thus any induction of this enzyme would be expected to counteract the therapeutic effect of hemin.', 'DDInter', 1767317475, 'Drugs capable of increasing the activity of delta-aminolevulinic acid synthetase should be avoided during hemin therapy.', 'Antagonism', 'Drugs capable of increasing the activity of delta-aminolevulinic acid synthetase should be avoided during hemin therapy.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363257, 'Hyaluronidase', 'Conjugated estrogens', 'Minor', 'Larger amounts of hyaluronidase may be required to achieve equivalent dispersing effects if patients are concomitantly receiving large doses of salicylates, cortisone, ACTH, estrogens, or antihistamines. These drugs may render tissues partially resistant to the dispersing action of hyaluronidase.', 'DDInter', 1767317475, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363258, 'Imatinib', 'Conjugated estrogens', 'Moderate', 'Coadministration with imatinib may increase the plasma concentrations of drugs that are substrates of CYP450 2C9, 2D6 and/or 3A4. The mechanism is decreased clearance due to inhibition of these isoenzymes by imatinib.', 'DDInter', 1767317475, 'Caution is advised when imatinib is used concomitantly with drugs that undergo metabolism by CYP450 2C9, 2D6 and/or 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever imatinib is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when imatinib is used concomitantly with drugs that undergo metabolism by CYP450 2C9, 2D6 and/or 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363259, 'Imipramine', 'Conjugated estrogens', 'Minor', 'The effects of tricyclic antidepressants (TCAs) may be altered in women receiving estrogen-containing therapy. Simultaneous TCA toxicity and reduced effects have been reported. Akathisia has also been reported in some women taking this combination. The mechanism of interaction is unknown but may be related to increased TCA bioavailability or inhibition of hepatic TCA metabolism.', 'DDInter', 1767317475, 'The clinical significance of this interaction has not been established. Monitoring for altered effects may be advisable during concomitant therapy. Dose adjustments of the TCA may be required if an interaction is suspected.', 'Others', 'The clinical significance of this interaction has not been established.', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363260, 'Indinavir', 'Conjugated estrogens', 'Minor', 'Coadministration with indinavir may increase the plasma concentrations of estrogens and progestins. The proposed mechanism is indinavir inhibition of CYP450 3A4, the isoenzyme involved in the metabolism of estrogens, some (if not all) progestins, and other steroids. The clinical significance of these alterations is unknown but should be considered when selecting oral contraceptive and hormone replacement doses.', 'DDInter', 1767317475, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
