INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363275, 'Isavuconazonium', 'Conjugated estrogens', 'Moderate', 'Coadministration with isavuconazonium sulfate (prodrug of isavuconazole) may increase the plasma concentrations of drugs that are substrates of CYP450 3A4 and/or P-glycoprotein (P-gp). The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity and/or P-gp-mediated efflux of these drugs by isavuconazole.', 'DDInter', 1767317475, 'Caution is advised when isavuconazonium sulfate is used concomitantly with drugs that are substrates of the CYP450 3A4 isoenzyme or the P-gp transport protein, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever isavuconazonium sulfate is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when isavuconazonium sulfate is used concomitantly with drugs that are substrates of the CYP450 3A4 isoenzyme or the P-gp transport protein, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363276, 'Itraconazole', 'Conjugated estrogens', 'Moderate', 'Azole antifungal agents may increase the plasma concentrations of estrogens and progestins. The mechanism is decreased clearance of the hormones due to inhibition of CYP450 3A4 activity by azole antifungals.', 'DDInter', 1767317475, 'During concomitant therapy with azole antifungal agents, patients should be observed for increased or altered pharmacologic response to estrogens and progestins, and dosage(s) adjusted accordingly as necessary.', 'Metabolism', 'During concomitant therapy with azole antifungal agents, patients should be observed for increased or altered pharmacologic response to estrogens and progestins, and dosage(s) adjusted accordingly as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363277, 'Ivacaftor', 'Conjugated estrogens', 'Moderate', 'Coadministration with ivacaftor may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme and/or P-glycoprotein (P-gp) efflux transporter. The mechanism is decreased clearance via these pathways due to inhibition by ivacaftor and its pharmacologically active M1 metabolite.', 'DDInter', 1767317475, 'Caution is advised when ivacaftor is used with drugs that are substrates of CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever ivacaftor is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when ivacaftor is used with drugs that are substrates of CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363278, 'Conjugated estrogens', 'Ivosidenib', 'Moderate', 'RECOMMENDED: Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of estrogens and progestins. Estrogens have been shown in in vitro and in vivo studies to be partially metabolized by CYP450 3A4, and other steroids including progestins are also believed to undergo metabolism by this isoenzyme.', 'DDInter', 1767317475, 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary. For patients receiving hormonal contraceptives, additional or alternative non-hormonal birth control may be advisable during concomitant therapy with CYP450 3A4 inducers. Additional or alternative non-hormonal birth control may be recommended beyond discontinuation of the CYP450 3A4 inducer(s).', 'Metabolism', 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363279, 'Conjugated estrogens', 'Ketoconazole', 'Moderate', 'Azole antifungal agents may increase the plasma concentrations of estrogens and progestins. The mechanism is decreased clearance of the hormones due to inhibition of CYP450 3A4 activity by azole antifungals.', 'DDInter', 1767317475, 'During concomitant therapy with azole antifungal agents, patients should be observed for increased or altered pharmacologic response to estrogens and progestins, and dosage(s) adjusted accordingly as necessary.', 'Metabolism', 'During concomitant therapy with azole antifungal agents, patients should be observed for increased or altered pharmacologic response to estrogens and progestins, and dosage(s) adjusted accordingly as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363280, 'Conjugated estrogens', 'Lamotrigine', 'Moderate', 'Coadministration with estrogens or progestins may decrease the plasma concentrations and pharmacologic effects of lamotrigine due to induction of lamotrigine glucuronidation. Lamotrigine also has been shown to have little or no effect on the pharmacokinetics of contraceptive hormones, although measurement of serum FSH, LH, and estradiol has indicated some loss of suppression of the hypothalamic-pituitary-ovarian axis. Measurement of serum progesterone indicated no hormonal evidence of ovulation.', 'DDInter', 1767317475, 'Pharmacologic response and plasma lamotrigine levels should be monitored more closely whenever estrogen- and/or progestin-containing drugs are added to or withdrawn from therapy, and the lamotrigine dosage adjusted as necessary. The manufacturer''s labeling should be consulted for detailed dosage recommendations. Patients should be advised to contact their physician if they experience loss of seizure control or symptoms of lamotrigine toxicity such as ataxia, nystagmus, increased seizures, irregular heartbeat, and changes in mental status. In patients receiving oral contraceptives, gradual transient increases in lamotrigine levels will occur during the pill-free week for women not also taking an enzyme-inducing drug (e.g., carbamazepine, phenytoin, phenobarbital, primidone, rifampin). The increase in lamotrigine levels will be greater if the dose of lamotrigine is increased in the few days before or during the pill-free week. Although diminished contraceptive efficacy has not been reported, the possibility should be considered. Patients should be instructed to promptly report changes in their menstrual pattern.', 'Antagonism', 'Pharmacologic response and plasma lamotrigine levels should be monitored more closely whenever estrogen- and/or progestin-containing drugs are added to or withdrawn from therapy, and the lamotrigine dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363281, 'Conjugated estrogens', 'Lapatinib', 'Moderate', 'Coadministration with lapatinib may increase the plasma concentrations of drugs that are substrates of the CYP450 2C8 isoenzyme, CYP450 3A4 isoenzyme, and/or P-glycoprotein (P-gp) efflux transporter. The mechanism is decreased clearance via these routes due to inhibition by lapatinib.', 'DDInter', 1767317475, 'Caution is advised if lapatinib must be used concurrently with medications that are substrates of the CYP450 2C8 isoenzyme, CYP450 3A4 isoenzyme, and/or P-gp efflux transporter, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lapatinib is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if lapatinib must be used concurrently with medications that are substrates of the CYP450 2C8 isoenzyme, CYP450 3A4 isoenzyme, and/or P-gp efflux transporter, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363282, 'Conjugated estrogens', 'Larotrectinib', 'Moderate', 'Coadministration with larotrectinib may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme.', 'DDInter', 1767317475, 'Caution is advised when larotrectinib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever larotrectinib is added to or withdrawn from therapy. Patients should be monitored for the development of adverse effects. Some authorities recommend avoiding concomitant use of larotrectinib and sensitive CYP450 3A4 substrates or substrates with narrow therapeutic ranges. These drugs include, but are not limited to: alfentanil, cisapride, colchicine, cyclosporine, fentanyl, ivacaftor, lovastatin, simvastatin, oral midazolam, triazolam, pimozide, quinidine, sildenafil, ergot derivatives, macrolide immunosuppressants, and vinca alkaloids.', 'Metabolism', 'Caution is advised when larotrectinib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363283, 'Conjugated estrogens', 'Lefamulin', 'Moderate', 'When administered orally, lefamulin may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4. Based on interaction with midazolam, a sensitive CYP450 3A4 substrate, lefamulin may be a moderate CYP450 3A4 inhibitor. When oral midazolam was administered concomitantly with and at 2 or 4 hours after administration of lefamulin tablets, mean midazolam peak plasma concentration (Cmax) and systemic exposure (AUC) increased by approximately 100% and 200%, respectively.', 'DDInter', 1767317475, 'Caution is advised when oral lefamulin is used with drugs that undergo metabolism via CYP450 3A4, particularly sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index (e.g., ergot alkaloids, colchicine, fentanyl, macrolide immunosuppressants, midazolam, triazolam, vardenafil, vinca alkaloids). Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever oral lefamulin is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when oral lefamulin is used with drugs that undergo metabolism via CYP450 3A4, particularly sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363284, 'Conjugated estrogens', 'Lemborexant', 'Moderate', 'RECOMMENDED: Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of estrogens and progestins. Estrogens have been shown in in vitro and in vivo studies to be partially metabolized by CYP450 3A4, and other steroids including progestins are also believed to undergo metabolism by this isoenzyme.', 'DDInter', 1767317475, 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary. For patients receiving hormonal contraceptives, additional or alternative non-hormonal birth control may be advisable during concomitant therapy with CYP450 3A4 inducers. Additional or alternative non-hormonal birth control may be recommended beyond discontinuation of the CYP450 3A4 inducer(s).', 'Metabolism', 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363285, 'Conjugated estrogens', 'Lenalidomide', 'Major', 'Concomitant treatment with agents that can cause thrombosis such as erythropoiesis-stimulating agents, estrogens, selective estrogen receptor modulators, or C1 esterase inhibitors may potentiate the risk of venous thromboembolic events associated with the use of lenalidomide. Deep vein thrombosis (DVT) and pulmonary embolism (PE) have been observed at significantly increased rates when lenalidomide was coadministered with dexamethasone for the treatment of multiple myeloma.', 'DDInter', 1767317475, 'Agents that are known to cause thrombosis should be used with caution in multiple myeloma patients receiving lenalidomide with dexamethasone. Patients should be advised to seek medical attention if they develop potential signs and symptoms of thromboembolism such as chest pain; shortness of breath; rapid pulse; pain, swelling, and/or discoloration in an arm or leg; and numbness or weakness on one side of the body. It is not known whether prophylactic anticoagulation or antiplatelet therapy may lessen the risk of venous thromboembolic events. The decision to take prophylactic measures should be done carefully after a thorough assessment of underlying risk factors. If a thromboembolic event occurs during therapy with lenalidomide, treatment must be discontinued and standard anticoagulation therapy initiated. Once anticoagulation is stabilized and complications of the thromboembolic event under control, lenalidomide may be restarted at the original dose if benefit is deemed to outweigh the risks. Anticoagulation therapy should be continued during the remaining course of lenalidomide treatment.', 'Synergism', 'Agents that are known to cause thrombosis should be used with caution in multiple myeloma patients receiving lenalidomide with dexamethasone.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363286, 'Conjugated estrogens', 'Letermovir', 'Moderate', 'Coadministration with letermovir may increase the plasma concentrations of drugs that are substrates of CYP450 3A4. In vitro and in vivo studies have shown that both estrogens and progestins are partially metabolized by CYP450 3A4. Studies of letermovir with midazolam, a CYP450 3A4 probe substrate, have shown the net effect of letermovir on the CYP450 3A4 isoenzyme is moderate inhibition.', 'DDInter', 1767317475, 'Monitor estrogen/progestin therapy for altered pharmacologic response and adjust dosage(s) accordingly whenever letermovir is added to or withdrawn from therapy. When choosing an oral contraceptive, consider choosing an ethinyl estradiol-levonorgestrel product as this combination has been studied with letermovir and no clinically significant interaction has been found.', 'Metabolism', 'Monitor estrogen/progestin therapy for altered pharmacologic response and adjust dosage(s) accordingly whenever letermovir is added to or withdrawn from therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363287, 'Conjugated estrogens', 'Letrozole', 'Moderate', 'Theoretically, estrogen-containing drugs may negate the pharmacologic effects of aromatase inhibitors in the treatment of hormone-dependent breast cancer. Aromatase inhibitors induce a state of estrogen deprivation in postmenopausal women by inhibiting the conversion of androgens to estrogens in adrenal and ovarian tissues, thus estrogen administration would be expected to reverse their therapeutic effects.', 'DDInter', 1767317475, 'Use of estrogen-containing products should be avoided during aromatase inhibitor therapy.', 'Antagonism', 'Use of estrogen-containing products should be avoided during aromatase inhibitor therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363288, 'Bazedoxifene', 'Levonorgestrel', 'Moderate', 'The use of bazedoxifene-conjugated estrogens in combination with progestins has not been evaluated. Safety and efficacy of this combination are unknown.', 'DDInter', 1767317475, 'Women treated with bazedoxifene-conjugated estrogens should not receive progestins, which have often been used to mitigate the risk of endometrial cancer associated with estrogen therapy in patients with an intact uterus. Since the bazedoxifene component is intended to reduce the risk of endometrial hyperplasia, a possible precursor to endometrial cancer, there should be no need for progestin therapy under such circumstances.', 'Others', 'Women treated with bazedoxifene-conjugated estrogens should not receive progestins, which have often been used to mitigate the risk of endometrial cancer associated with estrogen therapy in patients with an intact uterus.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363289, 'Conjugated estrogens', 'Levothyroxine', 'Moderate', 'Estrogens may increase serum thyrotropin concentration, which could be harmful in patients with thyroid cancer receiving thyroxine for thyrotropin suppression or increase dosage requirements in patients with hypothyroidism receiving thyroxine for replacement therapy. Estrogens are known to increase serum thyroid-binding globulin concentration in a dose-dependent manner. Consequently, there may be a reduction in unbound, or free, thyroxine available for hormone activity, which, in turn, leads to an increase in serum thyrotropin concentration. Normally, thyroxine secretion can increase to compensate for this effect, but patients with hypothyroidism lack the mechanism to adapt.', 'DDInter', 1767317475, 'In patients treated with thyroxine, serum thyrotropin should be measured approximately 12 weeks after estrogen therapy is initiated, changed or discontinued, and the thyroxine dosage adjusted accordingly. Patients should be advised to contact their physician if clinical manifestations of hypothyroidism occur, such as fatigue, cold intolerance, constipation, unexplained weight gain, depression, joint or muscle pain, thinning hair or hair loss, dry skin, hoarseness, and abnormal menstrual periods.', 'Distribution', 'In patients treated with thyroxine, serum thyrotropin should be measured approximately 12 weeks after estrogen therapy is initiated, changed or discontinued, and the thyroxine dosage adjusted accordingly.', NULL, NULL, 'Moderate', 'pharmacodynamic');
