        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1363380, 'Bedaquiline', 'Phenobarbital', 'Major', 'Coadministration with potent or moderate inducers of CYP450 3A4 may decrease the plasma concentrations of bedaquiline, which is primarily metabolized by CYP450 3A4 to the major N-monodesmethyl metabolite, M2. Because M2 is 4- to 6-fold less active than the parent drug in terms of antimycobacterial potency, the interaction may diminish the therapeutic effect of bedaquiline.', NULL, 'The concomitant use of bedaquiline with potent or moderate CYP450 3A4 inducers should generally be avoided.', NULL, 'The concomitant use of bedaquiline with potent or moderate CYP450 3A4 inducers should generally be avoided.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
