    ) VALUES (1363386, 'Boceprevir', 'Phenobarbital', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of boceprevir, which is partially metabolized by the isoenzyme.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic drug levels, concomitant use of boceprevir with potent CYP450 3A4 inducers is considered contraindicated.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic drug levels, concomitant use of boceprevir with potent CYP450 3A4 inducers is considered contraindicated.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
