INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (132341, 'Talimogene laherparepvec', 'Lomustine', 'Major', 'Talimogene laherparepvec is a live, attenuated herpes simplex virus. Administration during immunosuppressant or intense antineoplastic therapy may be associated with a risk of potentially life-threatening disseminated herpetic infection due to enhanced virus replication in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', NULL, 'Talimogene laherparepvec should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, 'Talimogene laherparepvec should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Treatment with talimogene laherparepvec may need to be deferred until after such therapy is discontinued and the patient''s immune system has sufficiently recovered.', 'Synergism', 'Pertuzumab, Rucaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Tazemetostat, Altretamine, Tagraxofusp, Ixazomib, Sotorasib, Enasidenib, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/132498/', '[1] "Product Information. Imlygic (talimogene laherparepvec)." Amgen USA, Thousand Oaks, CA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (132342, 'Temozolomide', 'Lomustine', 'Moderate', 'The concomitant or sequential administration of multiple antineoplastic agents may result in additive toxicities, particularly in the bone marrow and gastrointestinal tract.', NULL, 'Close clinical and laboratory monitoring for hematologic and nonhematologic toxicities are recommended when antineoplastic agents are administered concurrently or during close intervals.', NULL, 'Close clinical and laboratory monitoring for hematologic and nonhematologic toxicities are recommended when antineoplastic agents are administered concurrently or during close intervals. Dosing adjustments may be necessary. The manufacturers'' recommendations and institutional protocols for dosage, treatment regimens, monitoring, and management of toxicities should be consulted.', 'Synergism', 'Lomustine, Uracil mustard', 'Uracil mustard, Mechlorethamine, Temozolomide', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/132499/', '[1] "Product Information. Fluorouracil injection (fluorouracil)." Roche Laboratories, Nutley, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[4] "Product Information. Paraplatin (carboplatin)." Bristol-Myers Squibb, Princeton, NJ.[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] "Product Information. Ellence (epirubicin)" Pharmacia and Upjohn, Kalamazoo, MI.[7] Agencia Espaola de Medicamentos y Productos Sanitarios Healthcare "Centro de informacin online de medicamentos de la AEMPS - CIMA. Available from: URL: https://cima.aemps.es/cima/publico/home.html." ([2018]):[8] "Product Information. Ifex (ifosfamide)." Bristol-Myers Squibb, Princeton, NJ.[9] "Product Information. Zanosar (streptozocin)." Pharmacia and Upjohn, Kalamazoo, MI.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (132343, 'Teriflunomide', 'Lomustine', 'Major', 'The use of leflunomide with other immunosuppressive or myelosuppressive agents may increase the risk of infections. The risk is thought to extend to teriflunomide, its principal active metabolite, because recommended dosages of both result in a similar range of plasma concentrations of teriflunomide. The recent, concomitant, or sequential use (without the recommended leflunomide washout period or procedure) of other agents known to induce hepatotoxicity may potentiate the risk of liver injury associated with leflunomide. The risk is thought to extend to teriflunomide, its principal active metabolite, because recommended dosages of both result in a similar range of plasma concentrations of teriflunomide.', NULL, 'Close monitoring is recommended if leflunomide or teriflunomide is used in patients who are currently receiving or have recently received other immunosuppressive and hepatotoxic agents, and vice versa.', NULL, 'Close monitoring is recommended if leflunomide or teriflunomide is used in patients who are currently receiving or have recently received other immunosuppressive and hepatotoxic agents, and vice versa. Due to the prolonged elimination half-life of leflunomide''s active metabolite, an interaction may occur even when these agents are initiated after the discontinuation of treatment with leflunomide or teriflunomide. Liver enzymes, bilirubin, platelet, white blood cell count, and hemoglobin or hematocrit should be evaluated at baseline and regularly during therapy.', 'Synergism', 'Cyclosporine, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Tacrolimus, Abatacept, Apremilast, Inebilizumab, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/132500/', '[1] "Product Information. Arava (leflunomide)." Hoechst Marion-Roussel Inc, Kansas City, MO.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (132344, 'Thalidomide', 'Lomustine', 'Major', 'Coadministration of thalidomide with glucocorticoids and/or antineoplastic agents in the treatment of malignancy may potentiate the risk of thromboembolism. The exact mechanism is unknown but likely multifactorial.', NULL, 'Close monitoring for DVT or pulmonary embolism is recommended in patients who require thalidomide therapy in combination with glucocorticoids and/or cytotoxic agents.', NULL, 'Close monitoring for DVT or pulmonary embolism is recommended in patients who require thalidomide therapy in combination with glucocorticoids and/or cytotoxic agents. Patients should be advised to seek medical attention if they develop potential signs and symptoms of thromboembolism such as chest pain, shortness of breath, and pain or swelling in the arms or legs. Prophylaxis with anticoagulants such as low-molecular weight heparins or warfarin may be appropriate, but the decision to take thromboprophylactic measures should be made after careful assessment of underlying risk factors. If a thromboembolic event occurs during therapy with thalidomide, treatment must be discontinued and standard anticoagulation therapy started. Once anticoagulation is stabilized and complications of the thromboembolic event under control, thalidomide may be restarted at the original dose if benefit is deemed to outweigh the risks.', 'Synergism', 'Cyclosporine, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Tacrolimus, Abatacept, Apremilast, Inebilizumab, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/132501/', '[1] Fine H