INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (132629, 'Imatinib', 'Lorlatinib', 'Moderate', 'Imatinib is a substrate as well as a potent competitive inhibitor of the CYP450 3A4 enzymatic pathway. Theoretically, pharmacokinetic interactions may occur during the coadministration of imatinib with drugs that act as both inducer and substrate of the metabolic pathway. However, data are currently insufficient to predict the potential consequences and clinical significance of such interactions.', NULL, 'During concomitant therapy, the possibility of a diminished therapeutic response to imatinib should be considered.', NULL, 'During concomitant therapy, the possibility of a diminished therapeutic response to imatinib should be considered. In addition, clinical and laboratory monitoring for potentially increased pharmacologic effects of concomitantly administered medications, particularly those with a narrow therapeutic range, may be appropriate.', 'Metabolism', 'Lenvatinib, Trametinib, Asciminib, Dacomitinib, Tivozanib, Alectinib, Binimetinib, Lorlatinib', 'Sunitinib, Regorafenib, Imatinib, Lenvatinib, Erlotinib, Trametinib, Nintedanib, Vandetanib, Afatinib, Alectinib, Dacomitinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/132786/', '[1] "Product Information. Gleevec (imatinib mesylate)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Gleevec (imatinib mesylate)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (132630, 'Indinavir', 'Lorlatinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of lorlatinib, which has been found to be metabolized primarily by CYP450 3A4 and UGT1A4 in vitro. Increased exposure to lorlatinib may increase the risk of central nervous system adverse effects (e.g., seizures, hallucinations, changes in cognitive function, mood disorders, mental status changes, sleep impairment), hyperlipidemia (hypercholesterolemia and hypertriglyceridemia), PR interval prolongation and atrioventricular (AV) block, and interstitial lung disease/pneumonitis.', NULL, 'Concomitant use of lorlatinib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of lorlatinib with potent CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, the initial dosage of lorlatinib should be reduced from 100 mg orally once daily to 75 mg orally once daily. In patients who have had a dosage reduction to 75 mg orally once daily due to adverse reactions, the lorlatinib dosage should be further reduced to 50 mg orally once daily upon initiation of a potent CYP450 3A4 inhibitor. After 3 plasma half-lives following discontinuation of the potent CYP450 3A4 inhibitor, the lorlatinib dosage may be increased to that used prior to initiation of the inhibitor.', 'Metabolism', 'Lamivudine, Cabotegravir, Simeprevir, Zanamivir, Valaciclovir, Oseltamivir, Acyclovir, Brincidofovir, Stavudine, Remdesivir, Elbasvir, More', 'Futibatinib, Alpelisib, Lenvatinib, Trametinib, Nintedanib, Vandetanib, Afatinib, Alectinib, Dacomitinib, Tivozanib, Sorafenib', 'Major', NULL, 'DDInter', 'pharm