INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (132704, 'Praziquantel', 'Lorlatinib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of praziquantel, which is a substrate of the isoenzyme.', NULL, 'Concomitant use of praziquantel with CYP450 3A4 inducers should generally be avoided, since therapeutically effective blood levels of praziquantel may not be achieved.', NULL, 'Concomitant use of praziquantel with CYP450 3A4 inducers should generally be avoided, since therapeutically effective blood levels of praziquantel may not be achieved. Alternative agents for schistosomiasis should be considered whenever possible in patients receiving treatment with a CYP450 3A4 inducer. Otherwise, clinical response to praziquantel should be closely monitored.', 'Metabolism', 'Triclabendazole, Oxamniquine', 'Entrectinib, Imatinib, Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Bosutinib, Lapatinib, Selumetinib, Vandetanib, Cobimetinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/132861/', '[1] Ridtitid W, Wongnawa M, Mahatthanatrakul W, Punyo J, Sunbhanich M "Rifampin markedly decreases plasma concentrations of praziquantel in healthy volunteers." Clin Pharmacol Ther 72 (2002): 505-13[2] Bittencourt PR, Gracia CM, Martins R, et al "Phenytoin and carbamazepine decrease oral bioavailability of praziquantel." Neurology 42 (1992): 492-6[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Biltricide (praziquantel)." Bayer, West Haven, CT.[5] Castro N, Medina R, Sotelo J, Jung H "Bioavailability of praziquantel increases with concomitant administration of food." Antimicrob Agents Ch