INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (132806, 'Voriconazole', 'Lorlatinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of lorlatinib, which has been found to be metabolized primarily by CYP450 3A4 and UGT1A4 in vitro. Increased exposure to lorlatinib may increase the risk of central nervous system adverse effects (e.g., seizures, hallucinations, changes in cognitive function, mood disorders, mental status changes, sleep impairment), hyperlipidemia (hypercholesterolemia and hypertriglyceridemia), PR interval prolongation and atrioventricular (AV) block, and interstitial lung disease/pneumonitis.', NULL, 'Concomitant use of lorlatinib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of lorlatinib with potent CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, the initial dosage of lorlatinib should be reduced from 100 mg orally once daily to 75 mg orally once daily. In patients who have had a dosage reduction to 75 mg orally once daily due to adverse reactions, the lorlatinib dosage should be further reduced to 50 mg orally once daily upon initiation of a potent CYP450 3A4 inhibitor. After 3 plasma half-lives following discontinuation of the potent CYP450 3A4 inhibitor, the lorlatinib dosage may be increased to that used prior to initiation of the inhibitor.', 'Metabolism', 'Micafungin, Amphotericin B, Oteseconazole, Flucytosine, Anidulafungin', 'Futibatinib, Trametinib, Nintedanib, Afatinib, Dacomitinib, Tivozanib, Alectinib, Binimetinib', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/132963/', '[1] "Product Information. 