INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (132971, 'Indinavir', 'Lovastatin', 'Major', 'Protease inhibitors (PIs) may significantly increase the plasma concentrations of certain HMG-CoA reductase inhibitors. The mechanism is PI inhibition of CYP450 3A4 metabolism. High levels of HMG-CoA reductase inhibitory activity in plasma are associated with an increased risk of musculoskeletal toxicity, including myopathy and rhabdomyolysis, which may be accompanied by acute renal failure secondary to myoglobinuria and may result in death.', NULL, 'Simvastatin and lovastatin are considered contraindicated in patients treated with indinavir due to the potential for severe interaction.', NULL, 'Simvastatin and lovastatin are considered contraindicated in patients treated with indinavir due to the potential for severe interaction. Red yeast rice (which contains lovastatin) should also be avoided in patients treated with PIs. Fluvastatin and pravastatin are probably safer alternatives, since they are not metabolized by CYP450 3A4. All patients treated with HMG-CoA reductase inhibitors should be advised to promptly report any unexplained muscle pain, tenderness, or weakness, particularly if accompanied by malaise or fever. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', 'Lamivudine, Paritaprevir, Tenofovir alafenamide, Maraviroc, Cabotegravir, Zanamivir, Rilpivirine, Dolutegravir, Brincidofovir, Elbasvir, Famciclovir, More', 'Gemfibrozil, Fenofibric acid, Mipomersen, Pitavastatin, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Cholestyramine, Clofibrate, Colestipol, More', 'Major', NULL, '