INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (66079, 'Ciprofloxacin', 'Methadone', 'Major', 'Coadministration with ciprofloxacin may increase the plasma concentrations and pharmacologic effects of methadone. The proposed mechanism is ciprofloxacin inhibition of methadone metabolism via CYP450 3A4 and, to a lesser extent, CYP450 1A2.', NULL, 'Caution is advised if methadone is prescribed in combination with ciprofloxacin.', NULL, 'Caution is advised if methadone is prescribed in combination with ciprofloxacin. Pharmacologic response to methadone should be closely monitored and the dosage adjusted accordingly, particularly following initiation or discontinuation of ciprofloxacin in patients who are stabilized on their methadone regimen. It may be advisable to avoid using high dosages of methadone (>100 mg/day) with ciprofloxacin. In general, ECG monitoring should be considered for all methadone-treated patients with risk factors such as heart or liver disease; conduction abnormalities (e.g., congenital long QT syndrome; heart block); electrolyte disturbances (hypokalemia, hypomagnesemia); concomitant use of drugs that may cause QT prolongation or electrolyte loss; concomitant use of CYP450 3A4 inhibitors; or use of methadone at dosages greater than 100 mg daily. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/66166/', '[1] Moody DE, Alburges ME, Parker RJ, Collings JM, Strong JM