INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (66099, 'Ciprofloxacin', 'Modafinil', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of modafinil (the racemate) and armodafinil (the R-enantiomer), which are both partially metabolized by the isoenzyme. Conversely, the plasma levels of some of these inhibitors may decrease, since many of them are also substrates of CYP450 3A4, and modafinil and armodafinil have been found to be modest inducers of CYP450 3A4. The clinical significance of this potential interaction is unknown. Clinical monitoring for altered effects of modafinil and armodafinil as well as the CYP450 3A4 inhibitor may be appropriate following addition or withdrawal of one or the other drug.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/66186/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Nuvigil (armodafinil)." Cephalon Inc, West Chester, PA.[3] "Product Information. Provigil (modafinil)." Cephalon, Inc, West Chester, PA.[4] "Product Information. Provigil (modafinil)." Cephalon, Inc, West Chester, PA.[5] "Product Information. Nuvigil (armodafinil)." Cephalon Inc, West Chester, PA.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (66100, 'Ciprofloxacin', 'Mycophenolate mofetil', 'Moderate', 'Theoretically, drugs that alter the gastrointestinal flora (e.g., antibiotics) may reduce the oral bioavailability of mycophenolic acid products. Some investigators believe that antimicrobials may interfere with the enterohepatic recirculation of mycophenolic acid by decreasing bacterial hydrolytic enzymes in the gastrointestinal tract that are responsible for regenerating mycophenolic acid from its glucuronide metabolite following first-pass metabolism.', NULL, 'Close clinical and laboratory monitoring for evidence of diminished immunosuppressive effect of mycophenolic acid is recommended during concomitant therapy with anti-infective agents.', NULL, 'Close clinical and laboratory monitoring for evidence of diminished immunosuppressive effect of mycophenolic acid is recommended during concomitant therapy with anti-infective agents.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/66187/', '[1] "Product Information. CellCept (mycophenolate mofetil)." Roche Laboratories, Nutley, NJ.[2] "Product Information. Myfortic (mycophenolic acid)." Novartis Pharmaceuticals, East Hanover, NJ.[3] "Product Information. Myfortic (mycophenolic acid)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767365205);
