INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (66182, 'Ciprofloxacin', 'Ramelteon', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 may increase the plasma concentrations and pharmacologic effects of ramelteon, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised when ramelteon is used with CYP450 1A2 inhibitors.', NULL, 'Caution is advised when ramelteon is used with CYP450 1A2 inhibitors. A reduction in the ramelteon dosage may be necessary in patients who experience excessive sedation or other adverse effects.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/66269/', '[1] "Product Information. Rozerem (ramelteon)." Takeda Pharmaceuticals America, Lincolnshire, IL.[2] "Product Information. Rozerem (ramelteon)." Takeda Pharmaceuticals America, Lincolnshire, IL.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (66183, 'Ciprofloxacin', 'Ranolazine', 'Moderate', 'Ranolazine can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution is recommended if ranolazine is used in combination with other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if ranolazine is used in combination with other drugs that can prolong the QT interval. Since the magnitude of QTc prolongation increases with increasing plasma concentrations of ranolazine, the maximum recommended dosage of 1000 mg twice daily should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. Ranolazine is contraindicated in patients with liver cirrhosis because of the profound effect on QT prolongation in this population.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/66270/', '[1] European Medicines Agency "CHMP Assessment Report for Latixa. International nonproprietary name: ranolazine. Procedure No. EMEA/H/C/805. Available from: URL: http://www.emea.europa.eu/humandocs/PDFs/EPAR/latixa/H-805-en6.pdf." ([cited 2008]):[2] "Product Information. Ranexa (ranolazine)." Calmoseptine Inc, Huntington Beach, CA.[3] "Product Information. Ranexa (ranolazine)." Calmoseptine Inc, Huntington Beach, CA.[4] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[5] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[6] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[7] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (66184, 'Ciprofloxacin', 'Rasagiline', 'Major', 'Coadministration with inhibitors of CYP450 1A2 may increase the plasma concentrations of rasagiline, which is primarily metabolized by the isoenzyme. Because the MAO-B selectivity of rasagiline may diminish with increasing dosage above the recommended range of 0.5 to 1 mg/day, there may be an increased risk of hypertensive crisis and other adverse reactions associated with nonselective inhibition of MAO if the maximum dosage is used in the presence of ciprofloxacin or other CYP450 1A2 inhibitors.', NULL, 'Rasagiline should be prescribed at a maximum dosage of 0.', NULL, 'Rasagiline should be prescribed at a maximum dosage of 0.5 mg/day during coadministration with moderate or potent CYP450 1A2 inhibitors. Patients should be advised to promptly seek medical attention if they experience potential signs and symptoms of a hypertensive crisis such as severe headache, visual disturbances, confusion, stupor or coma, seizures, chest pain, unexplained nausea or vomiting, and stroke-like symptoms.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/66271/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Azilect (rasagiline)." Teva Pharmaceuticals USA, North Wales, PA.[4] McGrath PJ, Stewart JW, Quitkin FM "A possible L-deprenyl induced hypertensive reaction." J Clin Psychopharmacol 9 (1989): 310-1[5] "Product Information. Azilect (rasagiline)." Teva Pharmaceuticals USA, North Wales, PA.[6] Sweet RA, Liebowitz MR, Holt CS, Heimberg RG "Potential in