INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (66213, 'Ciprofloxacin', 'Sevelamer', 'Moderate', 'INTERVAL: Concurrent administration of sevelamer hydrochloride may decrease the oral bioavailability of ciprofloxacin and other quinolones, potentially reducing antibiotic efficacy and increasing the risk of bacterial resistance. The proposed mechanism is reduced quinolone absorption due to chelation with the protonated amine groups of sevelamer in the intestine.', NULL, 'Oral quinolone antibiotics should not be administered simultaneously with sevelamer hydrochloride.', NULL, 'Oral quinolone antibiotics should not be administered simultaneously with sevelamer hydrochloride. The optimal length of time needed to separate ingestion of these drugs is currently unknown. Based on the fact that it took up to 4 hours to achieve ciprofloxacin Cmax in all 15 volunteers of the study, the authors suggested a separation time of at least 4 hours between sevelamer and ciprofloxacin administration to reduce the likelihood of a significant interaction, particularly in patients with end-stage renal disease whose gastrointestinal motility may be slowed.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/66300/', '[1] Kays MB, Overholser BR, Mueller BA, Moe SM, Sowinski KM "Effects of sevelamer hydrochloride and calcium acetate on the oral bioavailability of ciprofloxacin." Am J Kidney Dis 42 (2003): 1253-9[2] "Product Information. Xatral (alfuzosin)." Sanofi-Synthelabo Canada Inc, Markham, ON.[3] "Product Information. Renagel (sevelamer)." Genzyme Corporation, Cambridge, MA.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (66214, 'Ciprofloxacin', 'Sevoflurane', 'Moderate', 'Sevoflurane may cause prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution is recommended if sevoflurane is used in combination with other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if sevoflurane is used in combination with other drugs that can prolong the QT interval. The patient''s cardiovascular status and serum electrolytes should be closely monitored.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/66301/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Ultane (sevoflurane)." Abbott Pharmaceutical, Abbott Park, IL.[4] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[6] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365205);
