INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (66254, 'Ciprofloxacin', 'Tizanidine', 'Major', 'Coadministration with ciprofloxacin may significantly increase the plasma concentrations and pharmacologic effects of tizanidine. The proposed mechanism is ciprofloxacin inhibition of tizanidine metabolism via CYP450 1A2.', NULL, 'Given the magnitude of the interaction, use of tizanidine with ciprofloxacin is considered contraindicated.', NULL, 'Given the magnitude of the interaction, use of tizanidine with ciprofloxacin is considered contraindicated. Other quinolones such as gatifloxacin, gemifloxacin, levofloxacin, lomefloxacin, or moxifloxacin may be safer alternatives in tizanidine-treated patients, since they are generally believed to have little, if any, effect on CYP450 1A2.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/66341/', '[1] Granfors MT, Backman JT, Laitila J, Neuvonen PJ "Tizanidine is mainly metabolized by cytochrome P450 1A2 in vitro." Br J Clin Pharmacol 57 (2004): 349-53[2] "Product Information. Cipro (ciprofloxacin)." Bayer, West Haven, CT.[3] "Product Information. Zanaflex (tizanidine)." Acorda Therapeutics, Hawthorne, NY.[4] Granfors MT, Backman JT, Neuvonen M, Neuvonen PJ "Ciprofloxacin greatly increases concentrations and hypotensive effect of tizanidine by inhibiting its cytochrome P450 1A2-mediated presystemic metabolism." Clin Pharmacol Ther 76 (2004): 598-606[5] Yuk JH, Nightingale CH, Sweeney KR, Quintiliani R, Lettieri JT, Forst RW "Relative bioavailability in healthy volunteers of ciprofloxacin administered through a nasogastric tube with and without enteral feeding." Antimicrob Agents Chemother 33 (1989):