INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (66463, 'Dofetilide', 'Cisplatin', 'Major', 'Dofetilide can cause dose- and concentration-related QT interval prolongation. Theoretically, coadministration with agents that can produce hypokalemia and/or hypomagnesemia (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins, stimulant laxatives) may result in elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsade de pointes, because of additive arrhythmogenic potential.', NULL, 'Caution is advised if dofetilide must be used concomitantly with medications that can cause potassium and/or magnesium disturbances.', NULL, 'Caution is advised if dofetilide must be used concomitantly with medications that can cause potassium and/or magnesium disturbances. Serum electrolytes should be evaluated and any abnormalities corrected prior to initiating therapy with dofetilide. During therapy, potassium concentrations should be kept above 4 mEq/dL and magnesium concentrations above 1.8 mg/dL. Patients should also have frequent ECGs and be monitored for serious arrhythmias when QT intervals are prolonged. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/66550/', '[1] "Product Information. Tikosyn (dofetilide)" Pfizer US Pharmaceuticals, Atlanta, GA.[2] "Product Information. Tikosyn (dofetilide)" Pfizer US Pharmaceuticals, Atlanta, GA.', 1767365205);
