INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363653, 'Thiothixene', 'Phenobarbital', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317475, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363654, 'Thyroid, porcine', 'Phenobarbital', 'Moderate', 'Barbiturates may decrease exogenous thyroid plasma concentrations resulting in elevated thyroid stimulating hormone (TSH) and exacerbation of hypothyroidism. The mechanism may be induction of hepatic CYP450 enzymes responsible for thyroxine (T4) and triiodothyronine (T3) metabolism. Clinical data are limited.', 'DDInter', 1767317475, 'Clinical monitoring of patient response, including laboratory serum TSH concentrations, is recommended. Adjustment of thyroid replacement dosage may be indicated when initiating or discontinuing barbiturate therapy.', 'Metabolism', 'Clinical monitoring of patient response, including laboratory serum TSH concentrations, is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363655, 'Ticagrelor', 'Phenobarbital', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of ticagrelor, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Concomitant use of ticagrelor with potent CYP450 3A4 inducers should generally be avoided.', 'Metabolism', 'Concomitant use of ticagrelor with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363656, 'Tofacitinib', 'Phenobarbital', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of tofacitinib, which is primarily metabolized by the isoenzyme. The interaction may result in loss of or reduced clinical response.', 'DDInter', 1767317475, 'The use of tofacitinib in combination with potent CYP450 3A4 inducers should generally be avoided. Alternative agents with no or minimal CYP450 3A4 induction potential are recommended whenever possible. The extent to which moderate CYP450 3A4 inducers interact with tofacitinib is unknown.', 'Metabolism', 'The use of tofacitinib in combination with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363657, 'Tolvaptan', 'Phenobarbital', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of tolvaptan, which is primarily metabolized by the isoenzyme. When tolvaptan was administered with the potent CYP450 3A4 inducer rifampin, tolvaptan peak plasma concentration (Cmax) and systemic exposure (AUC) decreased by approximately 85%.', 'DDInter', 1767317475, 'Concomitant use of tolvaptan with potent CYP450 3A4 inducers should generally be avoided. If coadministration is necessary, a dosage increase of tolvaptan may be required. Patient response should be closely monitored and the dosage adjusted accordingly.', 'Metabolism', 'Concomitant use of tolvaptan with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363658, 'Topiramate', 'Scopolamine', 'Major', 'Certain drugs such as carbonic anhydrase inhibitors and drugs with anticholinergic activity (e.g., antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, disopyramide) may potentiate the risk of oligohidrosis and hyperthermia associated occasionally with the use of topiramate, particularly in pediatric patients. These agents may alter electrolyte and fluid balance (carbonic anhydrase inhibition), inhibit peripheral sweating mechanisms (anticholinergic effect), and/or interfere with core body temperature regulation in the hypothalamus (neuroleptics and phenothiazines), resulting in the inability to adjust to temperature changes, especially in hot weather. Also, agents with anticholinergic activity frequently cause drowsiness and other central nervous system-depressant effects, which may be additively or synergistically increased in patients also treated with topiramate.', 'DDInter', 1767317475, 'Caution is advised when topiramate is prescribed with other drugs that predispose patients to heat-related disorders, including carbonic anhydrase inhibitors and drugs with anticholinergic activity. Patients, particularly pediatric patients, should be monitored closely for evidence of decreased sweating and increased body temperature, especially in warm or hot weather. Proper hydration before and during vigorous activities or exposure to warm temperatures is recommended. Patients (or their guardians or caregivers) should contact their physician immediately if they are not sweating as usual, with or without a fever. Ambulatory patients treated with topiramate and agents with anticholinergic activity should also be made aware of the possibility of additive CNS effects (e.g., drowsiness, dizziness, lightheadedness, confusion) and counseled to avoid activities requiring mental alertness until they know how these agents affect them.', 'Synergism', 'Caution is advised when topiramate is prescribed with other drugs that predispose patients to heat-related disorders, including carbonic anhydrase inhibitors and drugs with anticholinergic activity.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363659, 'Toremifene', 'Phenobarbital', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of toremifene.', 'DDInter', 1767317475, 'Concomitant use of toremifene with potent CYP450 3A4 inducers should generally be avoided. Alternative therapeutic agents with less enzyme induction potential should be considered whenever possible during treatment with toremifene. If coadministration is required, a doubling of the toremifene dosage may be necessary depending on patient tolerability. Close monitoring for toxicities (e.g., QT prolongation, hepatotoxicity, hypercalcemia and tumor flare) is recommended if the dosage of toremifene is increased. The dosage should be reduced to the indicated dosage following discontinuation of the potent CYP450 3A4 inducer.', 'Metabolism', 'Concomitant use of toremifene with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363660, 'Trabectedin', 'Phenobarbital', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may decrease the plasma concentrations of trabectedin, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Given the potential for diminished pharmacologic effects of trabectedin in the presence of potent CYP450 3A4 inducers, concomitant use should generally be avoided.', 'Metabolism', 'Given the potential for diminished pharmacologic effects of trabectedin in the presence of potent CYP450 3A4 inducers, concomitant use should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363661, 'Tranylcypromine', 'Phenobarbital', 'Moderate', 'Limited clinical data suggest that nonselective monoamine oxidase inhibitors (MAOIs) may enhance or prolong the central nervous system effects of barbiturates. The mechanism of interaction is unknown, but may involve inhibition of barbiturate metabolism by MAOIs. Somnolence, dizziness, ataxia, and semi-consciousness have been reported.', 'DDInter', 1767317475, 'Until more information is available, patients should be monitored for increased barbiturate effects during coadministration with an MAOI or other agents that possess MAOI activity such as furazolidone, linezolid, methylene blue, and procarbazine.', 'Metabolism', 'Until more information is available, patients should be monitored for increased barbiturate effects during coadministration with an MAOI or other agents that possess MAOI activity such as furazolidone, linezolid, methylene blue, and procarbazine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
