INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363870, 'Denosumab', 'Belimumab', 'Moderate', 'Concomitant use of immunosuppressive or myelosuppressive agents with denosumab may increase the risk of serious infections. Denosumab binds to and inhibits the receptor activator of nuclear factor kappa-B ligand (RANKL), which is expressed on activated T and B lymphocytes and in lymph nodes. Thus, denosumab alone may increase the risk of infections.', 'DDInter', 1767317475, 'Caution is advised if denosumab must be used in combination with immuno- or myelosuppressive agents. Patients should be advised to contact their physician if they develop signs and symptoms of infection such as fever, chills, diarrhea, sore throat, muscle aches, shortness of breath, blood in phlegm, weight loss, red or inflamed skin, body sores, and pain or burning during urination. The need for continued denosumab therapy should be assessed when serious infections occur during treatment.', 'Synergism', 'Caution is advised if denosumab must be used in combination with immuno- or myelosuppressive agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363871, 'Dimethyl fumarate', 'Belimumab', 'Moderate', 'The use of monomethyl fumarate (MMF) or its prodrugs, dimethyl fumarate and diroximel fumarate, with other immunosuppressive or myelosuppressive agents may increase the risk of infections.', 'DDInter', 1767317475, 'The safety and efficacy of monomethyl fumarate, dimethyl fumarate, or diroximel fumarate in combination with other immunosuppressive or myelosuppressive agents have not been evaluated. Until further information is available, concomitant use should be avoided if possible.', 'Synergism', 'The safety and efficacy of monomethyl fumarate, dimethyl fumarate, or diroximel fumarate in combination with other immunosuppressive or myelosuppressive agents have not been evaluated.', NULL, NULL, 'Moderate', 'pharmacodynamic');
