INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (133016, 'Rifampicin', 'Lovastatin', 'Major', 'Coadministration with rifampin may significantly decrease the plasma concentrations of lovastatin and simvastatin and their pharmacologically active acid metabolites. The primary mechanism is rifampin induction of intestinal and hepatic CYP450 3A4 metabolism, resulting in reduced bioavailability of simvastatin and lovastatin and their metabolites. Induction of intestinal P-glycoprotein efflux transporters may also contribute.', NULL, 'Given the magnitude of interaction, concomitant use of simvastatin, lovastatin, or red yeast rice (which contains lovastatin) with rifampin may result in significantly diminished hypolipidemic efficacy and should be avoided if possible.', NULL, 'Given the magnitude of interaction, concomitant use of simvastatin, lovastatin, or red yeast rice (which contains lovastatin) with rifampin may result in significantly diminished hypolipidemic efficacy and should be avoided if possible. Pravastatin may be a suitable substitution, as it is not a substrate of CYP450 enzymes or P-glycoprotein. One study reported only a 31% decrease in the mean AUC of single-dose pravastatin following pretreatment with rifampin, and the interaction did not occur in all subjects.', 'Metabolism', 'Fenofibric acid, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Cholestyramine, Ezetimibe, Cerivastatin, Colestipol, Niacin, Fenofibrate, More', 'Capreomycin, Pyrazinamide, Rifamycin, Sulfamethoxazole, Streptomycin, Cycloserine, Rifabutin, Pyridoxine, Pretomanid, Aminosalicylic acid', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/133173/', '[1] Everett DW, Chando TJ, Did