INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (133032, 'St. John''s Wort', 'Lovastatin', 'Moderate', 'Coadministration with St. John''s wort may decrease the plasma concentrations of certain HMG-CoA reductase inhibitors and/or their metabolites. The interaction has been reported specifically with simvastatin and its pharmacologically active acid metabolite. The mechanism is enhanced first-pass metabolism of simvastatin due to induction of hepatic and intestinal CYP450 3A4 activity by constituents of St. John''s wort. Reduced absorption of simvastatin secondary to induction of intestinal P-glycoprotein drug efflux pumps may also play a role.', NULL, 'While clinical data are lacking, the possibility of a diminished therapeutic response to simvastatin should be considered in patients simultaneously treated with St.', NULL, 'While clinical data are lacking, the possibility of a diminished therapeutic response to simvastatin should be considered in patients simultaneously treated with St. John''s wort. Other HMG-CoA reductase inhibitors that are substrates of CYP450 3A4 and P-glycoprotein such as lovastatin and atorvastatin are also expected to interact similarly with St. John''s wort. Fluvastatin, pravastatin, and rosuvastatin are not expected to participate in this interaction and may be suitable alternatives in patients using St. John''s wort.', 'Metabolism', 'Cholestyramine, Rosuvastatin, Fenofibrate, Mipomersen, Evinacumab, Dextrothyroxine, Probucol, Cerivastatin, Lomitapide, Ezetimibe, Niacin, More', 'Phenelzine, Desipramine, Tranylcypromine, Vilazodone, Trimipramine, Desvenlafaxine, Maprotiline, Isocarboxazid, Oxitriptan, Tryptophan, Vortioxetine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NU