INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (133866, 'Aminoglutethimide', 'Lurbinectedin', 'Moderate', 'Coadministration with a CYP450 3A4 inducer may decrease lurbinectedin systemic exposure, which may reduce its efficacy. No drug interaction studies are available for lurbinectedin, but in vitro data suggest that lurbinectedin is metabolized by the CYP450 3A4 isoenzyme.', NULL, 'Clinical and laboratory monitoring is advised whenever a CYP450 3A4 inducer is added to or withdrawn from lurbinectedin therapy.', NULL, 'Clinical and laboratory monitoring is advised whenever a CYP450 3A4 inducer is added to or withdrawn from lurbinectedin therapy.', 'Metabolism', 'Darolutamide, Tamoxifen, Anastrozole, Letrozole, Toremifene, Fulvestrant, Abarelix, Flutamide, Relugolix, Degarelix, Nilutamide, Exemestane', 'Pertuzumab, Oxaliplatin, Rucaparib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Pegaspargase, Enasidenib, Belzutifan, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/134023/', '[1] "Product Information. Zepzelca (lurbinectedin)." Jazz Pharmaceuticals, Palo Alto, CA.[2] "Product Information. Zepzelca (lurbinectedin)." Jazz Pharmaceuticals, Palo Alto, CA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (133867, 'Amprenavir', 'Lurbinectedin', 'Major', 'Coadministration with a strong CYP450 3A4 inhibitor is predicted to increase lurbinectedin systemic exposure, which may increase the incidence and severity of adverse reactions to lurbinectedin, such as myelosuppression and hepatotoxicity.', NULL, 'Concomitant use of lurbinectedin and strong CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of lurbinectedin and strong CYP450 3A4 inhibitors should generally be avoided.', 'Metabolism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Velpatasvir, More', 'Pertuzumab, Oxaliplatin, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Enasidenib, Porfimer sodium, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/134024/', '[1] "Product Information. Zepzelca (lurbinectedin)." Jazz Pharmaceuticals, Palo Alto, CA.[2] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.[3] "Product Information. Zepzelca (lurbinectedin)." Jazz Pharmaceuticals, Palo Alto, CA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (133868, 'Bacillus calmette-guerin substrain tice live antigen', 'Lurbinectedin', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids. These patients may also have increased adverse reactions and decreased or suboptimal immunologic response to vaccines.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy. A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide). Current local immunization guidelines should be consulted for recommendations.', 'Others', 'Pertuzumab, Aminolevulinic acid, Tazemetostat, Tagraxofusp, Sotorasib, Enasidenib, Belzutifan, Mitotane, Porfimer sodium, Anagrelide, Ivosidenib, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.s