INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (133968, 'Pegfilgrastim', 'Lurbinectedin', 'Moderate', 'INTERVAL: The safety and efficacy of hematopoietic growth factors such as colony-stimulating factors (G-CSF and GM-CSF) and stem cell factors (SCF) given simultaneously with cancer chemotherapy have not been established. Theoretical concerns exist regarding their concomitant administration because hematopoietic growth factors stimulate myeloid cell proliferation while antineoplastic agents primarily target rapidly dividing cells.', NULL, 'Because of the potential sensitivity of rapidly dividing myeloid cells to cancer chemotherapy, the manufacturer recommends that pegfilgrastim not be given from 14 days before until 24 hours after administration of antineoplastic agents.', NULL, 'Because of the potential sensitivity of rapidly dividing myeloid cells to cancer chemotherapy, the manufacturer recommends that pegfilgrastim not be given from 14 days before until 24 hours after administration of antineoplastic agents.', 'Antagonism', 'Interferon alfa-n1, Oprelvekin, Elapegademase, Peginterferon alfa-2a, Interferon alfacon-1, Histamine, Pegademase, Peginterferon alfa-2b, Glatiramer', 'Pertuzumab, Aminolevulinic acid, Tazemetostat, Tagraxofusp, Sotorasib, Enasidenib, Belzutifan, Mitotane, Porfimer sodium, Aflibercept, Anagrelide, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/134125/', '[1] "Product Information. Neulasta (pegfilgrastim)." Amgen, Thousand Oaks, CA.[2] "Product Information. Brukinsa (zanubrutinib)." BeiGene USA, Inc, San Mateo, CA.', 1767369485);
