INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363920, 'Oxitriptan', 'Lorcaserin', 'Major', 'Concomitant use of lorcaserin with agents that possess or enhance serotonergic activity such as selective serotonin reuptake inhibitors (SSRIs), selective serotonin-norepinephrine reuptake inhibitors (SNRIs), monoamine oxidase inhibitors (MAOIs), tricyclic antidepressants (TCAs), 5-HT1 receptor agonists (triptans), ergot alkaloids, phenylpiperidine opioids, bupropion, dextromethorphan, linezolid, lithium, St. John''s wort, tramadol, and tryptophan may potentiate the risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors.', 'DDInter', 1767317475, 'Caution is advised if lorcaserin must be used in combination with other agents that affect the serotonergic neurotransmitter system. Patients should be closely monitored for symptoms of the serotonin syndrome, which may include mental status changes such as irritability, altered consciousness, confusion, hallucinations, and coma; autonomic dysfunction such as tachycardia, hyperthermia, diaphoresis, shivering, blood pressure lability, and mydriasis; neuromuscular abnormalities such as hyperreflexia, myoclonus, tremor, rigidity, and ataxia; and gastrointestinal symptoms such as abdominal cramping, nausea, vomiting, and diarrhea.', 'Synergism', 'Caution is advised if lorcaserin must be used in combination with other agents that affect the serotonergic neurotransmitter system.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363921, 'Acetohexamide', 'Lorcaserin', 'Moderate', 'Since weight loss may affect glycemic control and increase the risk of hypoglycemia in type 2 diabetes mellitus, patients receiving anorectic drugs may require periodic adjustments of their antidiabetic medications.', 'DDInter', 1767317475, 'Blood glucose should be closely monitored during weight loss treatment, and appropriate changes made to the antidiabetic drug regimen as needed. Patients should be apprised of the risk of hypoglycemia and be alert to potential signs and symptoms such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia.', 'Synergism', 'Blood glucose should be closely monitored during weight loss treatment, and appropriate changes made to the antidiabetic drug regimen as needed.', NULL, NULL, 'Moderate', 'pharmacodynamic');
