INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363942, 'Dihydroergotamine', 'Lorcaserin', 'Major', 'Coadministration of lorcaserin with drugs that enhance 5-HT2B activity may increase the risk of valvular heart disease. Regurgitant cardiac valvular disease, primarily affecting the mitral and/or aortic valves, has been reported in patients who took serotonergic drugs with 5-HT2B receptor agonist activity. The etiology is thought to involve activation of 5-HT2B receptors on cardiac interstitial cells.', 'DDInter', 1767317475, 'Lorcaserin should not be used in combination with serotonergic and dopaminergic drugs that are potent 5-HT2B receptor agonists and are known to increase the risk of cardiac valvulopathy (e.g., cabergoline, pergolide, ergot alkaloids). Patients who develop signs and symptoms of valvular heart disease such as dizziness, fatigue, weakness, dyspnea, edema, or arrhythmia during treatment with lorcaserin should seek prompt medical attention. Discontinuation of lorcaserin should be considered.', 'Synergism', 'Lorcaserin should not be used in combination with serotonergic and dopaminergic drugs that are potent 5-HT2B receptor agonists and are known to increase the risk of cardiac valvulopathy (e.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363943, 'Diphenhydramine', 'Lorcaserin', 'Moderate', 'Coadministration with lorcaserin may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 2D6. The mechanism is decreased clearance due to inhibition of CYP450 2D6 activity by lorcaserin.', 'DDInter', 1767317475, 'Caution is advised if lorcaserin is prescribed concomitantly with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lorcaserin is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if lorcaserin is prescribed concomitantly with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363944, 'Donepezil', 'Lorcaserin', 'Minor', 'Coadministration with inhibitors of CYP450 2D6 and/or 3A4 may increase the plasma concentrations of donepezil, which is primarily metabolized by these isoenzymes.', 'DDInter', 1767317475, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1363945, 'Doxepin', 'Lorcaserin', 'Major', 'Concomitant use of lorcaserin with agents that possess or enhance serotonergic activity such as selective serotonin reuptake inhibitors (SSRIs), selective serotonin-norepinephrine reuptake inhibitors (SNRIs), monoamine oxidase inhibitors (MAOIs), tricyclic antidepressants (TCAs), 5-HT1 receptor agonists (triptans), ergot alkaloids, phenylpiperidine opioids, bupropion, dextromethorphan, linezolid, lithium, St. John''s wort, tramadol, and tryptophan may potentiate the risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors. Coadministration with lorcaserin may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 2D6, including many antidepressants (e.g., SSRIs, TCAs, trazodone), dextromethorphan, and tramadol. The mechanism is decreased clearance due to inhibition of CYP450 2D6 activity by lorcaserin.', 'DDInter', 1767317475, 'Caution is advised if lorcaserin must be used in combination with other agents that affect the serotonergic neurotransmitter system. Patients should be closely monitored for symptoms of the serotonin syndrome, which may include mental status changes such as irritability, altered consciousness, confusion, hallucinations, and coma; autonomic dysfunction such as tachycardia, hyperthermia, diaphoresis, shivering, blood pressure lability, and mydriasis; neuromuscular abnormalities such as hyperreflexia, myoclonus, tremor, rigidity, and ataxia; and gastrointestinal symptoms such as abdominal cramping, nausea, vomiting, and diarrhea. Particular caution is advised when initiating or increasing the dosages of these agents. The potential risk for serotonin syndrome should be considered even when administering serotonergic agents sequentially, as some agents may demonstrate a prolonged elimination half-life. For example, a 5-week washout period is typically recommended following use of fluoxetine and 2 weeks following use of MAOIs before administering another serotonergic agent. If serotonin syndrome develops or is suspected during the course of therapy, all serotonergic agents should be discontinued immediately and supportive care rendered as necessary.', 'Metabolism, Synergism', 'Caution is advised if lorcaserin must be used in combination with other agents that affect the serotonergic neurotransmitter system.', NULL, NULL, 'Major', 'pharmacodynamic');
