INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1364024, 'Pergolide', 'Lorcaserin', 'Major', 'Coadministration of lorcaserin with drugs that enhance 5-HT2B activity may increase the risk of valvular heart disease. Regurgitant cardiac valvular disease, primarily affecting the mitral and/or aortic valves, has been reported in patients who took serotonergic drugs with 5-HT2B receptor agonist activity. The etiology is thought to involve activation of 5-HT2B receptors on cardiac interstitial cells.', 'DDInter', 1767317475, 'Lorcaserin should not be used in combination with serotonergic and dopaminergic drugs that are potent 5-HT2B receptor agonists and are known to increase the risk of cardiac valvulopathy (e.g., cabergoline, pergolide, ergot alkaloids). Patients who develop signs and symptoms of valvular heart disease such as dizziness, fatigue, weakness, dyspnea, edema, or arrhythmia during treatment with lorcaserin should seek prompt medical attention. Discontinuation of lorcaserin should be considered.', 'Synergism', 'Lorcaserin should not be used in combination with serotonergic and dopaminergic drugs that are potent 5-HT2B receptor agonists and are known to increase the risk of cardiac valvulopathy (e.', NULL, NULL, 'Major', 'pharmacodynamic');
