INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1364066, 'Timolol', 'Lorcaserin', 'Moderate', 'Coadministration with lorcaserin may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 2D6. The mechanism is decreased clearance due to inhibition of CYP450 2D6 activity by lorcaserin.', 'DDInter', 1767317475, 'Caution is advised if lorcaserin is prescribed concomitantly with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lorcaserin is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if lorcaserin is prescribed concomitantly with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1364067, 'Timolol (ophthalmic)', 'Lorcaserin', 'Moderate', 'Coadministration with inhibitors of CYP450 2D6 may increase the systemic effects of topically administered timolol, which is metabolized by the isoenzyme. Following ocular administration, timolol is systemically absorbed and can reach plasma levels associated with adverse beta-adrenergic blocking effects such as bronchospasm, depression, bradycardia, and hypotension. The risk may be increased if clearance of the drug is significantly diminished by concomitant CYP450 2D6 inhibitors.', 'DDInter', 1767317475, 'Patients should be monitored for systemic beta-adrenergic blocking effects of topical timolol during coadministration with CYP450 2D6 inhibitors such as cimetidine, quinidine, and certain selective serotonin reuptake inhibitors. Particular caution is warranted in elderly patients, since they are generally more susceptible to adverse effects of topically administered beta blockers.', 'Metabolism', 'Patients should be monitored for systemic beta-adrenergic blocking effects of topical timolol during coadministration with CYP450 2D6 inhibitors such as cimetidine, quinidine, and certain selective serotonin reuptake inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1364068, 'Tolazamide', 'Lorcaserin', 'Moderate', 'Since weight loss may affect glycemic control and increase the risk of hypoglycemia in type 2 diabetes mellitus, patients receiving anorectic drugs may require periodic adjustments of their antidiabetic medications.', 'DDInter', 1767317475, 'Blood glucose should be closely monitored during weight loss treatment, and appropriate changes made to the antidiabetic drug regimen as needed. Patients should be apprised of the risk of hypoglycemia and be alert to potential signs and symptoms such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia.', 'Synergism', 'Blood glucose should be closely monitored during weight loss treatment, and appropriate changes made to the antidiabetic drug regimen as needed.', NULL, NULL, 'Moderate', 'pharmacodynamic');
