INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1364079, 'Vardenafil', 'Lorcaserin', 'Moderate', 'Theoretical concerns exist that coadministration of lorcaserin and drugs used for the treatment of erectile dysfunction may increase the risk of priapism (painful erections greater than 6 hours in duration). Lorcaserin may cause priapism due to 5-HT2C receptor agonism. If not treated promptly, priapism can result in irreversible damage to the erectile tissue.', 'DDInter', 1767317475, 'There is limited experience with the combination of lorcaserin and medications indicated for erectile dysfunction. Caution is advised if coadministration is necessary, particularly in patients with risk factors for priapism such as sickle cell anemia, multiple myeloma, leukemia, or anatomical deformation of the penis (e.g., angulation, cavernosal fibrosis, Peyronie''s disease). Men who have an erection lasting longer than 4 hours, whether painful or not, should immediately discontinue these drugs and seek emergency medical attention.', 'Synergism', 'There is limited experience with the combination of lorcaserin and medications indicated for erectile dysfunction.', NULL, NULL, 'Moderate', 'pharmacodynamic');
