INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1364126, 'Anisindione', 'Ezetimibe', 'Minor', 'In a study of 12 healthy adult males, coadministration of ezetimibe (10 mg once daily) had no significant effect on the bioavailability of warfarin or prothrombin time. Increased International Normalized Ratio (INR) has been reported during postmarketing use of ezetimibe in patients who had it added to warfarin. However, most of these patients were also on other medications, so the relationship to ezetimibe is uncertain. Ezetimibe product labeling recommends that the INR be appropriately monitored if ezetimibe is used in patients receiving warfarin. The effect of ezetimibe on other oral anticoagulants is unknown.', 'DDInter', 1767317475, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1364127, 'Ezetimibe', 'Cerivastatin', 'Moderate', 'Coadministration with ezetimibe may rarely increase the risk of myopathy and serum transaminase elevations associated with HMG-CoA reductase inhibitors (i.e., statins). The mechanism of interaction is unknown.', 'DDInter', 1767317475, 'Until further information is available, use of a statin in combination with ezetimibe should be approached with caution. Patients should be advised to promptly report to their physician any unexplained muscle pain, tenderness, or weakness, particularly if accompanied by malaise or fever. The drugs should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed. In addition, liver function tests should be performed at initiation of therapy and according to the recommendations of the HMG-CoA reductase inhibitor.', 'Others', 'Until further information is available, use of a statin in combination with ezetimibe should be approached with caution.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1364128, 'Ezetimibe', 'Chenodeoxycholic acid', 'Moderate', 'Drugs like estrogens, oral contraceptives, fibrates, and possibly other lipid-lowering agents that increase biliary cholesterol secretion may potentiate the risk of cholesterol gallstones and counteract the effectiveness of chenodeoxycholic acid.', 'DDInter', 1767317475, 'Therapeutic effects of chenodeoxycholic acid should be closely monitored if concomitant use with estrogens, oral contraceptives, fibrates, and certain other lipid-lowering agents cannot be avoided.', 'Others', 'Therapeutic effects of chenodeoxycholic acid should be closely monitored if concomitant use with estrogens, oral contraceptives, fibrates, and certain other lipid-lowering agents cannot be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1364129, 'Ezetimibe', 'Cholestyramine', 'Moderate', 'INTERVAL: In a study of 40 healthy hypercholesterolemic adults, concomitant administration of cholestyramine (4 g twice daily) decreased the mean area under the plasma concentration-time curve (AUC) of total ezetimibe by approximately 55%. The incremental LDL cholesterol reduction expected with the addition of ezetimibe to cholestyramine or other bile acid sequestrants may be diminished by this interaction.', 'DDInter', 1767317475, 'Ezetimibe should be administered at least 2 hours before or 4 hours after the bile acid sequestrant.', 'Others', 'Ezetimibe should be administered at least 2 hours before or 4 hours after the bile acid sequestrant.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1364130, 'Ezetimibe', 'Clofibrate', 'Moderate', 'The safety and efficacy of ezetimibe coadministered with fibrates other than fenofibrate have not been studied. Fibrates can increase cholesterol excretion into the bile and cause cholelithiasis.', 'DDInter', 1767317475, 'Due to the lack of safety and efficacy data, coadministration of ezetimibe with fibrates other than fenofibrate is not recommended. If cholelithiasis is suspected in a patient receiving such a combination, gallbladder studies are indicated and alternative lipid-lowering therapy should be considered.', 'Others', 'Due to the lack of safety and efficacy data, coadministration of ezetimibe with fibrates other than fenofibrate is not recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
