INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1364133, 'Ezetimibe', 'Cyclosporine', 'Moderate', 'Coadministration with cyclosporine may significantly increase the plasma concentrations of ezetimibe. The mechanism of interaction is unknown.', 'DDInter', 1767317475, 'Caution is advised if ezetimibe is prescribed to patients receiving cyclosporine due to the potential for increased exposure to ezetimibe and altered exposure to cyclosporine. This exposure may be greater in patients with severe renal impairment. Monitoring of cyclosporine concentrations is recommended.', 'Others', 'Caution is advised if ezetimibe is prescribed to patients receiving cyclosporine due to the potential for increased exposure to ezetimibe and altered exposure to cyclosporine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1364134, 'Ezetimibe', 'Darolutamide', 'Moderate', 'Coadministration with darolutamide may increase the plasma concentrations of drugs that are substrates of the organic anion transporting polypeptides (OATP) 1B1 and 1B3. The proposed mechanism, based on in vitro data, involves decreased metabolic clearance due to inhibition of OATP 1B1- and 1B3-mediated hepatic uptake by darolutamide. Increased adverse reactions of the OATP 1B1 and 1B3 substrates may result.', 'DDInter', 1767317475, 'Caution is advised when darolutamide is used concurrently with drugs that are known OATP 1B1 and 1B3 substrates. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever darolutamide is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when darolutamide is used concurrently with drugs that are known OATP 1B1 and 1B3 substrates.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1364135, 'Ezetimibe', 'Dicoumarol', 'Minor', 'In a study of 12 healthy adult males, coadministration of ezetimibe (10 mg once daily) had no significant effect on the bioavailability of warfarin or prothrombin time. Increased International Normalized Ratio (INR) has been reported during postmarketing use of ezetimibe in patients who had it added to warfarin. However, most of these patients were also on other medications, so the relationship to ezetimibe is uncertain. Ezetimibe product labeling recommends that the INR be appropriately monitored if ezetimibe is used in patients receiving warfarin. The effect of ezetimibe on other oral anticoagulants is unknown.', 'DDInter', 1767317475, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1364136, 'Ezetimibe', 'Eltrombopag', 'Moderate', 'Coadministration with eltrombopag may increase the plasma concentrations of drugs that are substrates of the organic anion transporting polypeptide (OATP) 1B1 and/or breast cancer resistance protein (BCRP) transporters. The mechanism is decreased clearance due to inhibition of OATP1B1-mediated hepatic uptake and BCRP-mediated intestinal and hepatobiliary efflux by eltrombopag. The interaction has been demonstrated for rosuvastatin, a known substrate of both OATP 1B1 and BCRP.', 'DDInter', 1767317475, 'Caution is advised during concomitant use of eltrombopag with drugs that are substrates of the OATP1B1 and/or BCRP transporters, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever eltrombopag is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised during concomitant use of eltrombopag with drugs that are substrates of the OATP1B1 and/or BCRP transporters, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1364137, 'Ezetimibe', 'Eluxadoline', 'Moderate', 'Coadministration with eluxadoline may increase the plasma concentrations of drugs that are substrates of the organic anion transporting polypeptide (OATP) 1B1 and/or breast cancer resistance protein (BCRP) transporters. The mechanism is decreased clearance due to inhibition of OATP1B1-mediated hepatic uptake and BCRP-mediated intestinal and hepatobiliary efflux by eluxadoline.', 'DDInter', 1767317475, 'Caution is advised when eluxadoline is used concomitantly with drugs that are substrates of the OATP1B1 and/or BCRP transporters, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever eluxadoline is added to or withdrawn from therapy.', 'Others', 'Caution is advised when eluxadoline is used concomitantly with drugs that are substrates of the OATP1B1 and/or BCRP transporters, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1364138, 'Ezetimibe', 'Enasidenib', 'Moderate', 'Based on in vitro data, coadministration with enasidenib may increase the plasma concentrations of drugs that are substrates of the transporters, organic anion transporting polypeptides (OATP) 1B1/1B3 and breast cancer resistance protein (BCRP). The proposed mechanism is decreased clearance due to enasidenib -mediated inhibition of OATP1B1/1B3 and BCRP transporters.', 'DDInter', 1767317475, 'Caution is advised when enasidenib is used concurrently with medications that are substrates of OATP 1B1/1B3 and/or BCRP, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever enasidenib is added to or withdrawn from therapy.', 'Distribution', 'Caution is advised when enasidenib is used concurrently with medications that are substrates of OATP 1B1/1B3 and/or BCRP, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1364139, 'Ezetimibe', 'Encorafenib', 'Moderate', 'Coadministration with encorafenib may theoretically increase the plasma concentrations of drugs that are substrates of P-glycoprotein (P-gp), breast cancer resistance protein (BCRP), organic cation transporter (OCT2), organic anion transporter (OAT1, OAT3), or organic anion transporting polypeptide (OATP1B1, OATP1B3). The proposed mechanism is decreased clearance due to inhibition of the corresponding transporter by encorafenib.', 'DDInter', 1767317475, 'Caution is advised if encorafenib must be used concomitantly with drugs that are substrates of the affected transporters, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever encorafenib is added to or withdrawn from therapy.', 'Excretion', 'Caution is advised if encorafenib must be used concomitantly with drugs that are substrates of the affected transporters, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1364140, 'Ezetimibe', 'Entrectinib', 'Moderate', 'Coadministration with entrectinib may increase the plasma concentrations of drugs that are substrates of the OATP 1B1 (organic anion transporting polypeptide 1B1) transporter. The proposed mechanism, based on in vitro data, is decreased clearance due to entrectinib-mediated inhibition of OATP1B1 transport protein.', 'DDInter', 1767317475, 'Caution is advised when entrectinib is prescribed with drugs that are OATP1B1 substrates, particularly those with a narrow therapeutic range such as atorvastatin, pravastatin, rosuvastatin, repaglinide, and bosentan. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever entrectinib is added to or withdrawn from therapy.', 'Excretion', 'Caution is advised when entrectinib is prescribed with drugs that are OATP1B1 substrates, particularly those with a narrow therapeutic range such as atorvastatin, pravastatin, rosuvastatin, repaglinide, and bosentan.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1364141, 'Flax seed', 'Ezetimibe', 'Moderate', 'Concomitant use of flaxseed oil with ezetimibe may lead to lower plasma alpha-linolenic acid (ALA) levels. The mechanism may be related to ezetimibe-mediated inhibition of intestinal absorption of ALA contained in flaxseed oil.', 'DDInter', 1767317475, 'Until further information is available, clinical or laboratory monitoring of the expected therapeutic benefits of flaxseed oil is recommended when ezetimibe is co-administered with flaxseed oil.', 'Absorption', 'Until further information is available, clinical or laboratory monitoring of the expected therapeutic benefits of flaxseed oil is recommended when ezetimibe is co-administered with flaxseed oil.', NULL, NULL, 'Moderate', 'pharmacodynamic');
